import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/UnifiedPayments.css';
import DogLoader from '../components/Loader';
import {
    DollarSign, TrendingUp, CheckCircle, Clock, Filter,
    Download, Calendar, CreditCard, Eye
} from 'lucide-react';

const UnifiedPayments = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [stats, setStats] = useState(null);
    const completed = stats?.completedCount ?? 0;
    const pending   = stats?.pendingCount   ?? 0;

    const [filters, setFilters] = useState({
        status: 'all',
        paymentMethod: 'all',
        startDate: '',
        endDate: ''
    });
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    /* ---------- auth ---------- */
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) { navigate('/auth'); return; }
        try {
            const { role } = jwtDecode(token);
            setUserRole(role);
        } catch {
            navigate('/auth');
        } finally {
            setLoading(false);
        }
    }, [navigate]);

    useEffect(() => { AOS.init({ duration: 600, once: true }); }, []);

    /* ---------- data ---------- */
    useEffect(() => {
        if (!userRole) return;
        loadStats();
        loadTransactions();
    }, [userRole, filters, page]);

    const loadStats = async () => {
        try {
            const token = localStorage.getItem('token');
            const url = userRole === 'admin'
                ? `${API_BASE_URL}/api/payment/admin/stats`
                : `${API_BASE_URL}/api/payment/stats`;
            const { data } = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            setStats(data.data);
        } catch (e) {
            toast.error('Kh√¥ng t·∫£i ƒë∆∞·ª£c th·ªëng k√™');
        }
    };

    const loadTransactions = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const params = new URLSearchParams({ page, limit: 20, ...filters });
            const url = userRole === 'admin'
                ? `${API_BASE_URL}/api/payment/admin/transactions?${params}`
                : `${API_BASE_URL}/api/payment/transactions?${params}`;
            const { data } = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            const list = userRole === 'admin' ? data.data.transactions : data.data;
            setTransactions(list);
            setTotalPages(data.data.pagination?.totalPages || 1);
        } catch (e) {
            toast.error('Kh√¥ng t·∫£i ƒë∆∞·ª£c giao d·ªãch');
        } finally {
            setLoading(false);
        }
    };

    /* ---------- helpers ---------- */
    const formatPrice = p => new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(p);
    const formatDate = d => new Date(d).toLocaleString('vi-VN');

    /* ---------- render ---------- */
    if (loading && !stats) return <DogLoader />;

    return (
        <div className="unified-payments">
            <Sidebar role={userRole} />
            <div className="main-area">
                <Header />
                <div className="content">
                    {/* header */}
                    <div className="page-header" data-aos="fade-down">
                        <h1>{userRole === 'admin' ? 'üí≥ Qu·∫£n l√Ω giao d·ªãch' : 'üí∞ L·ªãch s·ª≠ thanh to√°n'}</h1>
                        <p>{userRole === 'admin' ? 'To√†n b·ªô giao d·ªãch & l·ª£i nhu·∫≠n' : 'Giao d·ªãch c·ªßa b·∫°n'}</p>
                    </div>

                    {/* stats */}
                    {stats && (
                        <div className="stats-grid" data-aos="fade-up">
                            <div className="stat-card">
                                <DollarSign size={28} />
                                <div>
                                    <div className="val">{formatPrice(stats.totalRevenue || 0)}</div>
                                    <div className="lbl">{userRole === 'admin' ? 'T·ªïng doanh thu' : 'Doanh thu b√°n'}</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <TrendingUp size={28} />
                                <div>
                                    <div className="val">{formatPrice(stats.totalPlatformFee || stats.totalEarned || 0)}</div>
                                    <div className="lbl">{userRole === 'admin' ? 'L·ª£i nhu·∫≠n' : 'Thu nh·∫≠p th·ª±c'}</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <CheckCircle size={28} />
                                <div>
                                    <div className="val">{completed}</div>
                                    <div className="lbl">Th√†nh c√¥ng</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <Clock size={28} />
                                <div>
                                    <div className="val">{pending}</div>
                                    <div className="lbl">Ch·ªù x·ª≠ l√Ω</div>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* filters */}
                    <div className="filters" data-aos="fade-up">
                        <select value={filters.status} onChange={e => setFilters({ ...filters, status: e.target.value })}>
                            <option value="all">T·∫•t c·∫£ tr·∫°ng th√°i</option>
                            <option value="COMPLETED">Ho√†n th√†nh</option>
                            <option value="PENDING">Ch·ªù</option>
                            <option value="FAILED">Th·∫•t b·∫°i</option>
                        </select>
                        <select value={filters.paymentMethod} onChange={e => setFilters({ ...filters, paymentMethod: e.target.value })}>
                            <option value="all">T·∫•t c·∫£ ph∆∞∆°ng th·ª©c</option>
                            <option value="MOMO">MOMO</option>
                            <option value="VNPAY">VNPay</option>
                            <option value="SANDBOX">Sandbox</option>
                        </select>
                        <input type="date" value={filters.startDate} onChange={e => setFilters({ ...filters, startDate: e.target.value })} />
                        <input type="date" value={filters.endDate} onChange={e => setFilters({ ...filters, endDate: e.target.value })} />
                        <button onClick={loadTransactions}><Filter size={16} /> L·ªçc</button>
                        <button onClick={() => { /* export */ }}><Download size={16} /> Xu·∫•t</button>
                    </div>

                    {/* table */}
                    <div className="table-wrapper" data-aos="fade-up">
                        {loading ? <DogLoader /> : transactions.length === 0 ? <p className="empty">Kh√¥ng c√≥ giao d·ªãch</p> : (
                            <table>
                                <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Ng√†y</th>
                                    <th>S·∫£n ph·∫©m</th>
                                    {userRole === 'admin' && <th>Ng∆∞·ªùi mua</th>}
                                    {userRole === 'admin' && <th>Ng∆∞·ªùi b√°n</th>}
                                    <th>Ph∆∞∆°ng th·ª©c</th>
                                    <th>S·ªë ti·ªÅn</th>
                                    <th>Tr·∫°ng th√°i</th>
                                </tr>
                                </thead>
                                <tbody>
                                {transactions.map(tx => (
                                    <tr key={tx._id}>
                                        <td><code>{tx._id.slice(-6)}</code></td>
                                        <td>{formatDate(tx.created_at)}</td>
                                        <td>{tx.marketplace_page_id?.title || '-'}</td>
                                        {userRole === 'admin' && <td>{tx.buyer_id?.name || tx.buyer_id?.email || '-'}</td>}
                                        {userRole === 'admin' && <td>{tx.seller_id?.name || tx.seller_id?.email || '-'}</td>}
                                        <td>{tx.payment_method}</td>
                                        <td><strong>{formatPrice(tx.amount)}</strong></td>
                                        <td><span className={`badge ${tx.status.toLowerCase()}`}>{tx.status}</span></td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        )}
                        {totalPages > 1 && (
                            <div className="pagination">
                                <button disabled={page === 1} onClick={() => setPage(p => p - 1)}>Prev</button>
                                <span>Page {page} / {totalPages}</span>
                                <button disabled={page === totalPages} onClick={() => setPage(p => p + 1)}>Next</button>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UnifiedPayments;
