/** * RESPONSIVE UTILS - FIXED 100% */

export const getScaleFactor = (fromMode, toMode) => {
    const widths = { desktop: 1200, tablet: 768, mobile: 375 };
    return widths[toMode] / widths[fromMode];
};

export const autoScaleSize = (element, fromMode, toMode) => {
    const scaleFactor = getScaleFactor(fromMode, toMode);
    const baseSize = element.size?.[fromMode] || { width: 200, height: 50 };

    // Section full-width, scale height
    if (element.type === 'section') {
        return {
            width: toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200,
            height: Math.round(baseSize.height * scaleFactor) || 400
        };
    }

    // Popup fixed width, scale height
    if (element.type === 'popup' || element.type === 'modal') {
        return {
            width: toMode === 'mobile' ? 340 : toMode === 'tablet' ? 600 : baseSize.width || 600,
            height: Math.round(baseSize.height * scaleFactor) || 400
        };
    }

    let scaledWidth = Math.round(baseSize.width * scaleFactor);
    let scaledHeight = Math.round(baseSize.height * scaleFactor);

    const minSizes = {
        button: { width: 80, height: 32 },
        icon: { width: 24, height: 24 },
        image: { width: 100, height: 100 },
        heading: { width: 150, height: 24 },
        paragraph: { width: 200, height: 40 },
        gallery: { width: 300, height: 200 },
        default: { width: 100, height: 40 }
    };

    const minSize = minSizes[element.type] || minSizes.default;

    if (toMode === 'mobile') {
        scaledWidth = Math.min(scaledWidth, 340);
    }

    return {
        width: Math.max(scaledWidth, minSize.width),
        height: Math.max(scaledHeight, minSize.height)
    };
};

export const autoScalePosition = (element, fromMode, toMode, newSize) => {
    const fromPos = element.position?.[fromMode] || { x: 0, y: 0, z: 1 };
    const scaleFactor = getScaleFactor(fromMode, toMode);

    // Section x=0, scale y
    if (element.type === 'section') {
        return { x: 0, y: Math.round(fromPos.y * scaleFactor), z: fromPos.z || 1 };
    }

    // Popup auto-center
    if (element.type === 'popup' || element.type === 'modal') {
        const canvasW = toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200;
        return {
            x: Math.round((canvasW - newSize.width) / 2),
            y: Math.round(fromPos.y * scaleFactor),
            z: fromPos.z || 1001
        };
    }

    let scaledX = Math.round(fromPos.x * scaleFactor);
    let scaledY = Math.round(fromPos.y * scaleFactor);

    const canvasWidth = toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200;
    const elementWidth = newSize?.width || 200;

    if (scaledX + elementWidth > canvasWidth) {
        scaledX = Math.max(0, canvasWidth - elementWidth - 10);
    }

    return { x: Math.max(0, scaledX), y: Math.max(0, scaledY), z: fromPos.z || 1 };
};

export const autoScaleStyles = (styles, fromMode, toMode) => {
    if (!styles) return {};

    const scaleFactor = getScaleFactor(fromMode, toMode);
    const scaledStyles = { ...styles };

    const scalableProps = [
        'fontSize', 'padding', 'paddingTop', 'paddingBottom', 'paddingLeft', 'paddingRight',
        'margin', 'marginTop', 'marginBottom', 'marginLeft', 'marginRight',
        'borderRadius', 'gap', 'borderWidth', 'strokeWidth', 'lineHeight'
    ];

    scalableProps.forEach(prop => {
        if (scaledStyles[prop]) {
            const value = scaledStyles[prop];
            if (typeof value === 'string') {
                const match = value.match(/^([\d.]+)(px|rem|em|%)$/);
                if (match) {
                    const num = parseFloat(match[1]);
                    const unit = match[2];
                    if (unit === 'px') {
                        const scaled = Math.round(num * scaleFactor * 10) / 10;
                        const min = prop.includes('font') ? 10 : 4;
                        scaledStyles[prop] = `${Math.max(scaled, min)}${unit}`;
                    }
                }
            } else if (typeof value === 'number') {
                const scaled = Math.round(value * scaleFactor);
                const min = prop.includes('font') ? 10 : 4;
                scaledStyles[prop] = Math.max(scaled, min);
            }
        }
    });

    if (scaledStyles.padding && typeof scaledStyles.padding === 'string' && scaledStyles.padding.includes(' ')) {
        const values = scaledStyles.padding.split(' ');
        const scaledValues = values.map(val => {
            const match = val.match(/^([\d.]+)(px|rem|em)$/);
            if (match) {
                const num = parseFloat(match[1]);
                const unit = match[2];
                const scaled = Math.round(num * scaleFactor * 10) / 10;
                return `${Math.max(scaled, 4)}${unit}`;
            }
            return val;
        });
        scaledStyles.padding = scaledValues.join(' ');
    }

    return scaledStyles;
};

export const autoScaleComponentData = (componentData, toMode) => {
    if (!componentData) return {};

    const scaled = { ...componentData };

    if (toMode === 'mobile') {
        if (scaled.columns && scaled.columns > 2) scaled.columns = 1;
        if (scaled.images && scaled.images.length > 0) scaled.gridTemplateColumns = 'repeat(2, 1fr)';
        if (scaled.gap) {
            const match = scaled.gap.match(/^([\d.]+)(px|rem)$/);
            if (match) scaled.gap = `${Math.max(parseFloat(match[1]) * 0.5, 8)}${match[2]}`;
        }
    } else if (toMode === 'tablet') {
        if (scaled.columns && scaled.columns > 3) scaled.columns = 2;
        if (scaled.images && scaled.images.length > 0) scaled.gridTemplateColumns = 'repeat(3, 1fr)';
    }

    return scaled;
};

/** * MAIN FUNCTION: Sync element giữa các view modes */
export const syncElementBetweenModes = (element, changedMode) => {
    const modes = ['desktop', 'tablet', 'mobile'];
    const updatedElement = { ...element };

    updatedElement.position = updatedElement.position || {
        desktop: { x: 0, y: 0, z: 1 },
        tablet: { x: 0, y: 0, z: 1 },
        mobile: { x: 0, y: 0, z: 1 }
    };
    updatedElement.size = updatedElement.size || {};
    updatedElement.responsiveStyles = updatedElement.responsiveStyles || {};
    updatedElement.componentData = updatedElement.componentData || {};

    modes.forEach(targetMode => {
        if (targetMode !== changedMode) {
            const scaledSize = autoScaleSize(updatedElement, changedMode, targetMode);
            const scaledPosition = autoScalePosition(updatedElement, changedMode, targetMode, scaledSize);

            updatedElement.size[targetMode] = scaledSize;
            updatedElement.position[targetMode] = scaledPosition;
        }
    });

    modes.filter(m => m !== 'desktop').forEach(mode => {
        updatedElement.responsiveStyles[mode] = autoScaleStyles(updatedElement.styles || {}, 'desktop', mode);
        updatedElement.componentData[mode] = autoScaleComponentData(updatedElement.componentData, mode);
    });

    // Sync children - chỉ 1 level để tránh loop
    if (updatedElement.children && updatedElement.children.length > 0) {
        updatedElement.children = updatedElement.children.map(child => {
            const updatedChild = { ...child };
            modes.forEach(mode => {
                updatedChild.size[mode] = updatedElement.size[mode] || child.size?.[mode];
                updatedChild.position[mode] = updatedElement.position[mode] || child.position?.[mode];
            });
            return updatedChild;
        });
    }

    return updatedElement;
};

export const syncAllElements = (pageData, currentMode) => {
    return {
        ...pageData,
        elements: pageData.elements.map(element => syncElementBetweenModes(element, currentMode))
    };
};

/** * Get responsive values dựa trên viewMode hiện tại */
export const getResponsiveValues = (element, viewMode) => {
    const size = element.size?.[viewMode] || {
        width: element.type === 'section' ? (viewMode === 'mobile' ? 375 : viewMode === 'tablet' ? 768 : 1200) : 200,
        height: 400
    };

    const position = element.position?.[viewMode] || { x: 0, y: 0, z: 1 };

    const styles = viewMode === 'desktop' ? (element.styles || {}) : {
        ...(element.styles || {}),
        ...(element.responsiveStyles?.[viewMode] || {})
    };

    let componentData = viewMode === 'desktop' ? (element.componentData || {}) : {
        ...(element.componentData || {}),
        ...(element.componentData[viewMode] || {})
    };

    // Đồng bộ background
    if (!componentData.backgroundType && styles.backgroundImage) {
        componentData.backgroundType = 'image';
        componentData.backgroundImage = styles.backgroundImage.replace(/^url\(["']?(.+?)["']?\)$/, '$1');
        // ... thêm các prop khác
    }

    return { id: element.id, type: element.type, size, position, styles, componentData, children: element.children || [], visible: element.visible !== false };
};

export const initializeResponsiveData = (pageData) => {
    return {
        ...pageData,
        elements: pageData.elements.map(element => {
            if (!element.position?.mobile || !element.position?.tablet) {
                return syncElementBetweenModes(element, 'desktop');
            }
            return element;
        })
    };
};

export const batchSyncElements = (elements, changedMode) => {
    return elements.map(element => syncElementBetweenModes(element, changedMode));
};

export default {
    getScaleFactor,
    autoScaleSize,
    autoScalePosition,
    autoScaleStyles,
    autoScaleComponentData,
    syncElementBetweenModes,
    syncAllElements,
    getResponsiveValues,
    initializeResponsiveData,
    batchSyncElements
};
