import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';

import DogLoader from '../components/Loader';
import { toast } from 'react-toastify';
import '../styles/AdminTemplate.css';
import Sidebar from "./Sidebar";

const Templates = () => {
    const navigate = useNavigate();
    const [userRole, setUserRole] = useState(null);
    const [templates, setTemplates] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isSaving, setIsSaving] = useState(false);
    const [showUploadModal, setShowUploadModal] = useState(false);
    const [showPreviewModal, setShowPreviewModal] = useState(false);
    const [showEditModal, setShowEditModal] = useState(false);
    const [selectedTemplate, setSelectedTemplate] = useState(null);
    const [previewHtml, setPreviewHtml] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [filterCategory, setFilterCategory] = useState('all');
    const [filterPremium, setFilterPremium] = useState('all');
    const [stats, setStats] = useState(null);

    // Upload form state
    const [uploadForm, setUploadForm] = useState({
        file: null,
        name: '',
        description: '',
        category: 'Thương mại điện tử',
        price: 0,
        tags: '',
        is_premium: false,
        is_featured: false
    });

    // Edit form state
    const [editForm, setEditForm] = useState({
        name: '',
        description: '',
        category: '',
        price: 0,
        tags: '',
        is_premium: false,
        is_featured: false,
        status: 'ACTIVE'
    });

    const categories = [
        'Thương mại điện tử',
        'Landing Page',
        'Blog',
        'Portfolio',
        'Doanh nghiệp',
        'Giáo dục',
        'Sự kiện',
        'Bất động sản',
        'Khác'
    ];
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true, offset: 100 });
    }, []);

    useEffect(() => {
        if (userRole && userRole === 'user') {
            setTemplates([]);
            setPage(1);
            setHasMore(true);
            loadTemplates(1);
        }
    }, [userRole, selectedCategory, searchQuery, designType, sortBy]);

    // Check auth
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui lòng đăng nhập để tiếp tục');
            navigate('/auth');
            return;
        }

        try {
            const decoded = jwtDecode(token);
            if (decoded.role !== 'admin') {
                toast.error('Bạn không có quyền truy cập trang này');
                navigate('/pages');
                return;
            }

            const currentTime = Math.floor(Date.now() / 1000);
            if (decoded.exp && decoded.exp < currentTime) {
                localStorage.removeItem('token');
                toast.error('Phiên đăng nhập đã hết hạn');
                navigate('/auth');
                return;
            }

            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        } catch (err) {
            console.error('Error decoding token:', err);
            toast.error('Phiên đăng nhập không hợp lệ');
            navigate('/auth');
        }
    }, [navigate]);

    // Fetch templates
    const fetchTemplates = useCallback(async () => {
        setIsLoading(true);
        try {
            const params = {};
            if (filterCategory !== 'all') params.category = filterCategory;
            if (filterPremium !== 'all') params.is_premium = filterPremium === 'premium';

            const response = await api.get('/api/templates', { params });
            if (response.data.success) {
                setTemplates(response.data.templates);
            }
        } catch (error) {
            console.error('Error fetching templates:', error);
            toast.error('Lỗi khi tải danh sách template: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsLoading(false);
        }
    }, [filterCategory, filterPremium]);

    // Fetch stats
    const fetchStats = useCallback(async () => {
        try {
            const response = await api.get('/api/templates/admin/stats');
            if (response.data.success) {
                setStats(response.data.stats);
            }
        } catch (error) {
            console.error('Error fetching stats:', error);
        }
    }, []);

    useEffect(() => {
        fetchTemplates();
        fetchStats();
    }, [fetchTemplates, fetchStats]);

    // Handle upload
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type === 'text/html') {
            setUploadForm({ ...uploadForm, file });
        } else {
            toast.error('Vui lòng chọn file HTML hợp lệ');
        }
    };

    const handleUploadSubmit = async (e) => {
        e.preventDefault();
        if (!uploadForm.file || !uploadForm.name) {
            toast.error('Vui lòng nhập đầy đủ thông tin');
            return;
        }

        setIsSaving(true);
        try {
            // Step 1: Get pre-signed URL
            const urlResponse = await api.get('/api/templates/admin/presigned-url');
            if (!urlResponse.data.success) {
                throw new Error('Không thể lấy URL upload');
            }

            const { templateId, uploadUrl, s3Path } = urlResponse.data;

            // Step 2: Read file content
            const fileContent = await uploadForm.file.text();

            // Step 3: Upload to S3
            await fetch(uploadUrl, {
                method: 'PUT',
                body: fileContent,
                headers: {
                    'Content-Type': 'text/html'
                }
            });

            // Step 4: Save metadata
            const tagsArray = uploadForm.tags
                .split(',')
                .map(tag => tag.trim())
                .filter(tag => tag.length > 0);

            const metadataResponse = await api.post('/api/templates/admin/metadata', {
                templateId,
                name: uploadForm.name,
                description: uploadForm.description,
                category: uploadForm.category,
                price: parseFloat(uploadForm.price) || 0,
                s3Path,
                tags: tagsArray,
                is_premium: uploadForm.is_premium,
                is_featured: uploadForm.is_featured
            });

            if (metadataResponse.data.success) {
                toast.success('Upload template thành công! Đang tạo screenshot...');
                setShowUploadModal(false);
                setUploadForm({
                    file: null,
                    name: '',
                    description: '',
                    category: 'Thương mại điện tử',
                    price: 0,
                    tags: '',
                    is_premium: false,
                    is_featured: false
                });
                fetchTemplates();
                fetchStats();
            }
        } catch (error) {
            console.error('Error uploading template:', error);
            toast.error('Lỗi khi upload template: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsSaving(false);
        }
    };

    // Handle preview
    const handlePreview = async (template) => {
        setIsLoading(true);
        try {
            const response = await api.get(`/api/templates/${template.id}/preview`);
            if (response.data.success) {
                setPreviewHtml(response.data.html);
                setSelectedTemplate(template);
                setShowPreviewModal(true);
            }
        } catch (error) {
            console.error('Error previewing template:', error);
            toast.error('Lỗi khi xem trước template: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsLoading(false);
        }
    };

    // Handle edit
    const handleEdit = (template) => {
        setSelectedTemplate(template);
        setEditForm({
            name: template.name,
            description: template.description,
            category: template.category,
            price: template.price,
            tags: template.tags?.join(', ') || '',
            is_premium: template.is_premium,
            is_featured: template.is_featured,
            status: template.status || 'ACTIVE'
        });
        setShowEditModal(true);
    };

    const handleEditSubmit = async (e) => {
        e.preventDefault();
        if (!selectedTemplate) return;

        setIsSaving(true);
        try {
            const tagsArray = editForm.tags
                .split(',')
                .map(tag => tag.trim())
                .filter(tag => tag.length > 0);

            const response = await api.put(`/api/templates/${selectedTemplate.id}`, {
                name: editForm.name,
                description: editForm.description,
                category: editForm.category,
                price: parseFloat(editForm.price) || 0,
                tags: tagsArray,
                is_premium: editForm.is_premium,
                is_featured: editForm.is_featured,
                status: editForm.status
            });

            if (response.data.success) {
                toast.success('Cập nhật template thành công!');
                setShowEditModal(false);
                fetchTemplates();
                fetchStats();
            }
        } catch (error) {
            console.error('Error updating template:', error);
            toast.error('Lỗi khi cập nhật template: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsSaving(false);
        }
    };

    // Handle regenerate screenshot
    const handleRegenerateScreenshot = async (templateId) => {
        setIsSaving(true);
        try {
            const response = await api.post(`/api/templates/${templateId}/regenerate-screenshot`);
            if (response.data.success) {
                toast.success('Tạo lại screenshot thành công!');
                fetchTemplates();
            }
        } catch (error) {
            console.error('Error regenerating screenshot:', error);
            toast.error('Lỗi khi tạo screenshot: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsSaving(false);
        }
    };

    // Handle batch regenerate
    const handleBatchRegenerate = async () => {
        if (!window.confirm('Bạn có chắc muốn tạo lại screenshot cho tất cả templates? Quá trình này có thể mất vài phút.')) {
            return;
        }

        setIsSaving(true);
        try {
            const response = await api.post('/api/templates/admin/batch-regenerate-screenshots');
            if (response.data.success) {
                toast.success(response.data.message);
                fetchTemplates();
            }
        } catch (error) {
            console.error('Error batch regenerating:', error);
            toast.error('Lỗi: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsSaving(false);
        }
    };

    // Handle delete
    const handleDelete = async (templateId) => {
        if (!window.confirm('Bạn có chắc muốn xóa template này? Hành động này không thể hoàn tác.')) {
            return;
        }

        setIsSaving(true);
        try {
            const response = await api.delete(`/api/templates/${templateId}`);
            if (response.data.success) {
                toast.success('Xóa template thành công!');
                fetchTemplates();
                fetchStats();
            }
        } catch (error) {
            console.error('Error deleting template:', error);
            toast.error('Lỗi khi xóa template: ' + (error.response?.data?.error || error.message));
        } finally {
            setIsSaving(false);
        }
    };

    // Filter templates
    const filteredTemplates = templates.filter(template => {
        const matchesSearch = template.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            template.description?.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesSearch;
    });

    if (isLoading && templates.length === 0) {
        return (
            <div className="flex h-screen">
                <div className="flex-1 flex items-center justify-center">
                    <DogLoader />
                </div>
            </div>
        );
    }

    return (
        <div className="flex h-screen bg-gray-50">
            <Sidebar>
            </Sidebar>
            <div className="flex-1 flex flex-col overflow-hidden">

                {/* Stats Cards */}
                {stats && (
                    <div className="px-6 py-4 bg-white border-b">
                        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                            <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg p-4 text-white">
                                <div className="text-sm opacity-90">Tổng Templates</div>
                                <div className="text-3xl font-bold mt-1">{stats.total}</div>
                            </div>
                            <div className="bg-gradient-to-br from-green-500 to-green-600 rounded-lg p-4 text-white">
                                <div className="text-sm opacity-90">Miễn phí</div>
                                <div className="text-3xl font-bold mt-1">{stats.free}</div>
                            </div>
                            <div className="bg-gradient-to-br from-yellow-500 to-yellow-600 rounded-lg p-4 text-white">
                                <div className="text-sm opacity-90">Premium</div>
                                <div className="text-3xl font-bold mt-1">{stats.premium}</div>
                            </div>
                            <div className="bg-gradient-to-br from-purple-500 to-purple-600 rounded-lg p-4 text-white">
                                <div className="text-sm opacity-90">Nổi bật</div>
                                <div className="text-3xl font-bold mt-1">{stats.featured}</div>
                            </div>
                            <div className="bg-gradient-to-br from-pink-500 to-pink-600 rounded-lg p-4 text-white">
                                <div className="text-sm opacity-90">Lượt sử dụng</div>
                                <div className="text-3xl font-bold mt-1">{stats.totalUsage}</div>
                            </div>
                        </div>
                    </div>
                )}

                {/* Toolbar */}
                <div className="px-6 py-4 bg-white border-b">
                    <div className="flex flex-wrap items-center gap-4">
                        <button
                            onClick={() => setShowUploadModal(true)}
                            className="px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all shadow-lg hover:shadow-xl flex items-center gap-2"
                        >
                            <i className="fas fa-upload"></i>
                            Upload Template
                        </button>

                        <button
                            onClick={handleBatchRegenerate}
                            className="px-4 py-2 bg-gradient-to-r from-purple-500 to-purple-600 text-white rounded-lg hover:from-purple-600 hover:to-purple-700 transition-all shadow-lg hover:shadow-xl flex items-center gap-2"
                        >
                            <i className="fas fa-camera"></i>
                            Tạo lại tất cả Screenshots
                        </button>

                        <input
                            type="text"
                            placeholder="Tìm kiếm template..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="flex-1 min-w-[200px] px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        />

                        <select
                            value={filterCategory}
                            onChange={(e) => setFilterCategory(e.target.value)}
                            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        >
                            <option value="all">Tất cả danh mục</option>
                            {categories.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>

                        <select
                            value={filterPremium}
                            onChange={(e) => setFilterPremium(e.target.value)}
                            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        >
                            <option value="all">Tất cả loại</option>
                            <option value="free">Miễn phí</option>
                            <option value="premium">Premium</option>
                        </select>
                    </div>
                </div>

                {/* Templates Grid */}
                <div className="flex-1 overflow-y-auto px-6 py-4">
                    {filteredTemplates.length === 0 ? (
                        <div className="text-center py-12">
                            <i className="fas fa-inbox text-6xl text-gray-300 mb-4"></i>
                            <p className="text-gray-500 text-lg">Không tìm thấy template nào</p>
                        </div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                            {filteredTemplates.map(template => (
                                <div key={template.id} className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all overflow-hidden group">
                                    {/* Thumbnail */}
                                    <div className="relative h-48 bg-gray-200 overflow-hidden">
                                        {template.thumbnail_url ? (
                                            <img
                                                src={template.thumbnail_url}
                                                alt={template.name}
                                                className="w-full h-full object-cover object-top group-hover:scale-105 transition-transform duration-300"
                                            />
                                        ) : (
                                            <div className="w-full h-full flex items-center justify-center">
                                                <i className="fas fa-image text-4xl text-gray-400"></i>
                                            </div>
                                        )}

                                        {/* Badges */}
                                        <div className="absolute top-2 left-2 flex gap-2">
                                            {template.is_featured && (
                                                <span className="px-2 py-1 bg-yellow-500 text-white text-xs rounded-full font-semibold">
                                                    <i className="fas fa-star mr-1"></i>Nổi bật
                                                </span>
                                            )}
                                            {template.is_premium && (
                                                <span className="px-2 py-1 bg-purple-500 text-white text-xs rounded-full font-semibold">
                                                    <i className="fas fa-crown mr-1"></i>Premium
                                                </span>
                                            )}
                                        </div>

                                        {/* Quick Actions Overlay */}
                                        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-50 transition-all flex items-center justify-center gap-2 opacity-0 group-hover:opacity-100">
                                            <button
                                                onClick={() => handlePreview(template)}
                                                className="px-3 py-2 bg-white text-gray-800 rounded-lg hover:bg-gray-100 transition-all"
                                                title="Xem trước"
                                            >
                                                <i className="fas fa-eye"></i>
                                            </button>
                                            <button
                                                onClick={() => handleEdit(template)}
                                                className="px-3 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all"
                                                title="Chỉnh sửa"
                                            >
                                                <i className="fas fa-edit"></i>
                                            </button>
                                            <button
                                                onClick={() => handleRegenerateScreenshot(template.id)}
                                                className="px-3 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-all"
                                                title="Tạo lại screenshot"
                                            >
                                                <i className="fas fa-camera"></i>
                                            </button>
                                        </div>
                                    </div>

                                    {/* Content */}
                                    <div className="p-4">
                                        <h3 className="font-semibold text-lg mb-1 truncate">{template.name}</h3>
                                        <p className="text-sm text-gray-600 mb-2 line-clamp-2">{template.description}</p>

                                        <div className="flex items-center justify-between text-sm text-gray-500 mb-3">
                                            <span className="bg-gray-100 px-2 py-1 rounded">{template.category}</span>
                                            <span className="font-semibold text-blue-600">{template.formatted_price}</span>
                                        </div>

                                        <div className="flex items-center justify-between text-xs text-gray-500 mb-3">
                                            <span><i className="fas fa-eye mr-1"></i>{template.usage_count} lượt dùng</span>
                                            <span>{template.created_at}</span>
                                        </div>

                                        {/* Tags */}
                                        {template.tags && template.tags.length > 0 && (
                                            <div className="flex flex-wrap gap-1 mb-3">
                                                {template.tags.slice(0, 3).map((tag, index) => (
                                                    <span key={index} className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded">
                                                        {tag}
                                                    </span>
                                                ))}
                                            </div>
                                        )}

                                        {/* Actions */}
                                        <div className="flex gap-2">
                                            <button
                                                onClick={() => handleEdit(template)}
                                                className="flex-1 px-3 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all text-sm"
                                            >
                                                <i className="fas fa-edit mr-1"></i>Sửa
                                            </button>
                                            <button
                                                onClick={() => handleDelete(template.id)}
                                                className="px-3 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-all text-sm"
                                            >
                                                <i className="fas fa-trash"></i>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Upload Modal */}
            {showUploadModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
                        <div className="p-6 border-b border-gray-200">
                            <div className="flex items-center justify-between">
                                <h2 className="text-2xl font-bold text-gray-800">
                                    <i className="fas fa-upload mr-2 text-blue-500"></i>
                                    Upload Template Mới
                                </h2>
                                <button
                                    onClick={() => setShowUploadModal(false)}
                                    className="text-gray-400 hover:text-gray-600 transition-colors"
                                >
                                    <i className="fas fa-times text-2xl"></i>
                                </button>
                            </div>
                        </div>

                        <form onSubmit={handleUploadSubmit} className="p-6 space-y-4">
                            {/* File Upload */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    <i className="fas fa-file-code mr-1"></i>
                                    File HTML *
                                </label>
                                <input
                                    type="file"
                                    accept=".html"
                                    onChange={handleFileChange}
                                    required
                                    className="w-full px-4 py-2 border-2 border-dashed border-gray-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                                />
                                <p className="text-xs text-gray-500 mt-1">
                                    Upload file HTML có chứa lpb-page-data để tự động extract pageData
                                </p>
                            </div>

                            {/* Name */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Tên Template *
                                </label>
                                <input
                                    type="text"
                                    value={uploadForm.name}
                                    onChange={(e) => setUploadForm({ ...uploadForm, name: e.target.value })}
                                    required
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Ví dụ: Landing Page Bất Động Sản Modern"
                                />
                            </div>

                            {/* Description */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Mô tả
                                </label>
                                <textarea
                                    value={uploadForm.description}
                                    onChange={(e) => setUploadForm({ ...uploadForm, description: e.target.value })}
                                    rows={3}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Mô tả ngắn gọn về template"
                                />
                            </div>

                            <div className="grid grid-cols-2 gap-4">
                                {/* Category */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                                        Danh mục
                                    </label>
                                    <select
                                        value={uploadForm.category}
                                        onChange={(e) => setUploadForm({ ...uploadForm, category: e.target.value })}
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    >
                                        {categories.map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Price */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                                        Giá (VNĐ)
                                    </label>
                                    <input
                                        type="number"
                                        value={uploadForm.price}
                                        onChange={(e) => setUploadForm({ ...uploadForm, price: e.target.value })}
                                        min="0"
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="0 = Miễn phí"
                                    />
                                </div>
                            </div>

                            {/* Tags */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Tags
                                </label>
                                <input
                                    type="text"
                                    value={uploadForm.tags}
                                    onChange={(e) => setUploadForm({ ...uploadForm, tags: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="modern, minimal, responsive (phân cách bằng dấu phẩy)"
                                />
                            </div>

                            {/* Checkboxes */}
                            <div className="flex gap-6">
                                <label className="flex items-center gap-2 cursor-pointer">
                                    <input
                                        type="checkbox"
                                        checked={uploadForm.is_featured}
                                        onChange={(e) => setUploadForm({ ...uploadForm, is_featured: e.target.checked })}
                                        className="w-4 h-4 text-blue-500 border-gray-300 rounded focus:ring-2 focus:ring-blue-500"
                                    />
                                    <span className="text-sm font-medium text-gray-700">
                                        <i className="fas fa-star mr-1 text-yellow-500"></i>
                                        Nổi bật
                                    </span>
                                </label>
                            </div>

                            {/* Submit */}
                            <div className="flex gap-3 pt-4 border-t border-gray-200">
                                <button
                                    type="button"
                                    onClick={() => setShowUploadModal(false)}
                                    className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-all"
                                >
                                    Hủy
                                </button>
                                <button
                                    type="submit"
                                    disabled={isSaving}
                                    className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
                                >
                                    {isSaving ? (
                                        <>
                                            <i className="fas fa-spinner fa-spin mr-2"></i>
                                            Đang upload...
                                        </>
                                    ) : (
                                        <>
                                            <i className="fas fa-upload mr-2"></i>
                                            Upload Template
                                        </>
                                    )}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Edit Modal */}
            {showEditModal && selectedTemplate && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
                        <div className="p-6 border-b border-gray-200">
                            <div className="flex items-center justify-between">
                                <h2 className="text-2xl font-bold text-gray-800">
                                    <i className="fas fa-edit mr-2 text-blue-500"></i>
                                    Chỉnh sửa Template
                                </h2>
                                <button
                                    onClick={() => setShowEditModal(false)}
                                    className="text-gray-400 hover:text-gray-600 transition-colors"
                                >
                                    <i className="fas fa-times text-2xl"></i>
                                </button>
                            </div>
                        </div>

                        <form onSubmit={handleEditSubmit} className="p-6 space-y-4">
                            {/* Name */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Tên Template *
                                </label>
                                <input
                                    type="text"
                                    value={editForm.name}
                                    onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                                    required
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                />
                            </div>

                            {/* Description */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Mô tả
                                </label>
                                <textarea
                                    value={editForm.description}
                                    onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                                    rows={3}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                />
                            </div>

                            <div className="grid grid-cols-2 gap-4">
                                {/* Category */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                                        Danh mục
                                    </label>
                                    <select
                                        value={editForm.category}
                                        onChange={(e) => setEditForm({ ...editForm, category: e.target.value })}
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    >
                                        {categories.map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Price */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                                        Giá (VNĐ)
                                    </label>
                                    <input
                                        type="number"
                                        value={editForm.price}
                                        onChange={(e) => setEditForm({ ...editForm, price: e.target.value })}
                                        min="0"
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    />
                                </div>
                            </div>

                            {/* Tags */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Tags
                                </label>
                                <input
                                    type="text"
                                    value={editForm.tags}
                                    onChange={(e) => setEditForm({ ...editForm, tags: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="modern, minimal, responsive (phân cách bằng dấu phẩy)"
                                />
                            </div>

                            {/* Status */}
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Trạng thái
                                </label>
                                <select
                                    value={editForm.status}
                                    onChange={(e) => setEditForm({ ...editForm, status: e.target.value })}
                                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                >
                                    <option value="ACTIVE">Hoạt động</option>
                                    <option value="INACTIVE">Tạm ẩn</option>
                                    <option value="DRAFT">Nháp</option>
                                </select>
                            </div>

                            {/* Checkboxes */}
                            <div className="flex gap-6">
                                <label className="flex items-center gap-2 cursor-pointer">
                                    <input
                                        type="checkbox"
                                        checked={editForm.is_premium}
                                        onChange={(e) => setEditForm({ ...editForm, is_premium: e.target.checked })}
                                        className="w-4 h-4 text-blue-500 border-gray-300 rounded focus:ring-2 focus:ring-blue-500"
                                    />
                                    <span className="text-sm font-medium text-gray-700">
                                        <i className="fas fa-crown mr-1 text-purple-500"></i>
                                        Premium
                                    </span>
                                </label>

                                <label className="flex items-center gap-2 cursor-pointer">
                                    <input
                                        type="checkbox"
                                        checked={editForm.is_featured}
                                        onChange={(e) => setEditForm({ ...editForm, is_featured: e.target.checked })}
                                        className="w-4 h-4 text-blue-500 border-gray-300 rounded focus:ring-2 focus:ring-blue-500"
                                    />
                                    <span className="text-sm font-medium text-gray-700">
                                        <i className="fas fa-star mr-1 text-yellow-500"></i>
                                        Nổi bật
                                    </span>
                                </label>
                            </div>

                            {/* Submit */}
                            <div className="flex gap-3 pt-4 border-t border-gray-200">
                                <button
                                    type="button"
                                    onClick={() => setShowEditModal(false)}
                                    className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-all"
                                >
                                    Hủy
                                </button>
                                <button
                                    type="submit"
                                    disabled={isSaving}
                                    className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
                                >
                                    {isSaving ? (
                                        <>
                                            <i className="fas fa-spinner fa-spin mr-2"></i>
                                            Đang lưu...
                                        </>
                                    ) : (
                                        <>
                                            <i className="fas fa-save mr-2"></i>
                                            Lưu thay đổi
                                        </>
                                    )}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Preview Modal */}
            {showPreviewModal && (
                <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-xl shadow-2xl w-full max-w-6xl h-[90vh] flex flex-col">
                        <div className="p-4 border-b border-gray-200 flex items-center justify-between">
                            <div>
                                <h2 className="text-xl font-bold text-gray-800">
                                    <i className="fas fa-eye mr-2 text-blue-500"></i>
                                    Xem trước: {selectedTemplate?.name}
                                </h2>
                                <p className="text-sm text-gray-500 mt-1">{selectedTemplate?.category}</p>
                            </div>
                            <button
                                onClick={() => {
                                    setShowPreviewModal(false);
                                    setPreviewHtml('');
                                }}
                                className="px-4 py-2 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-lg transition-all"
                            >
                                <i className="fas fa-times mr-2"></i>
                                Đóng
                            </button>
                        </div>
                        <div className="flex-1 overflow-hidden">
                            <iframe
                                srcDoc={previewHtml}
                                className="w-full h-full border-none"
                                title="Template Preview"
                                sandbox="allow-scripts allow-same-origin"
                            />
                        </div>
                    </div>
                </div>
            )}

            {/* Loading Overlay */}
            {isSaving && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <DogLoader />
                </div>
            )}
        </div>
    );
};

export default Templates;
