import React, { useState, useEffect, useContext } from 'react';
// Import các icon từ lucide-react
import {
    FileText, Edit3, List, Tag, DollarSign,
    Link2, Info, HelpCircle, CheckCircle,
    AlertCircle, TrendingUp, Shield, Clock,
    Home, Package, Settings,
    User, LogOut, LayoutList
} from 'lucide-react';
// Import CSS
import '../styles/SellPage.css';

// Mock contexts và components (Bạn sẽ thay thế bằng import thật)
const UserContext = React.createContext({ user: { role: 'seller' } });
const Header = () => <div style={{ height: '60px', background: '#192A56', color: 'white', display: 'flex', alignItems: 'center', padding: '0 20px', fontSize: '1.2rem', fontWeight: '600' }}>Marketplace Platform</div>;

// Component Sidebar CHI TIẾT ĐÃ ĐƯỢC CẬP NHẬT
const sidebarItems = {
    seller: [
        { name: 'Dashboard', icon: Home, link: '/dashboard' },
        { name: 'Quản lý Pages', icon: LayoutList, link: '/pages' },
        { name: 'Đăng bán Page', icon: Package, link: '/sell', highlight: true },
        { name: 'Giao dịch', icon: DollarSign, link: '/transactions' },
        { name: 'Doanh số', icon: TrendingUp, link: '/analytics' },
    ],
};

const commonItems = [
    { name: 'Tài khoản', icon: User, link: '/profile' },
    { name: 'Cài đặt', icon: Settings, link: '/settings' },
    { name: 'Trợ giúp', icon: HelpCircle, link: '/help' }
];

const Sidebar = ({ userRole }) => {
    const roleItems = sidebarItems[userRole] || sidebarItems.seller;
    const allItems = [...roleItems, ...commonItems];

    const handleLogout = () => {
        // Giả lập logic đăng xuất
        console.log("Logout triggered");
        // localStorage.removeItem('token'); // Uncomment trong app thật
        // window.location.href = '/auth'; // Uncomment trong app thật
    };

    return (
        <div className="sidebar">
            <div className="sidebar-header">
                <div className="sidebar-logo">MARKETPLACE</div>
                <div className="user-info">
                    Chào, <strong>{userRole.toUpperCase()}</strong>
                </div>
            </div>
            <nav className="sidebar-nav">
                <ul>
                    {allItems.map((item, index) => {
                        const Icon = item.icon;
                        return (
                            <li key={index} className={`nav-item ${item.link === '/sell' ? 'active' : ''}`}>
                                <a href={item.link} className={item.highlight ? 'highlight-link' : ''}>
                                    <Icon size={20} />
                                    <span>{item.name}</span>
                                </a>
                            </li>
                        );
                    })}
                    <li className="nav-item logout-item">
                        <button onClick={handleLogout}>
                            <LogOut size={20} />
                            <span>Đăng xuất</span>
                        </button>
                    </li>
                </ul>
            </nav>
        </div>
    );
};

const DogLoader = () => <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2rem' }}>⏳ Loading...</div>;

// --- TOOLTIP COMPONENT ---
const Tooltip = ({ children, text }) => {
    const [show, setShow] = useState(false);
    return (
        <div className="tooltip-container">
            <div
                onMouseEnter={() => setShow(true)}
                onMouseLeave={() => setShow(false)}
                className="tooltip-target"
            >
                {children}
            </div>
            {show && (
                <div className="tooltip-content">
                    {text}
                </div>
            )}
        </div>
    );
};

// --- SELL PAGE MAIN COMPONENT ---
const SellPage = () => {
    // Giả lập các state cần thiết
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(false);
    // Lấy userRole từ Context hoặc giả lập mặc định là 'seller'
    const [userRole, setUserRole] = useState(user?.role || 'seller');
    const [submitting, setSubmitting] = useState(false);
    const [testMode, setTestMode] = useState(true);
    const [myPages] = useState([
        { _id: '1', name: 'Landing Page Pro', status: 'ĐÃ XUẤT BẢN', description: 'Professional landing page', url: 'https://example.com/pro-demo' },
        { _id: '2', name: 'E-commerce Store', status: 'NHÁP', description: 'Modern store template', url: 'https://example.com/store-demo' }
    ]);
    const [formData, setFormData] = useState({
        page_id: '',
        title: '',
        description: '',
        category: 'Landing Page',
        price: '',
        original_price: '',
        tags: '',
        demo_url: ''
    });

    // Giả lập categories
    const categories = [
        'Landing Page', 'Thương mại mại điện tử', 'Blog', 'Portfolio',
        'Doanh nghiệp', 'Giáo dục', 'Sự kiện', 'Bất động sản',
        'Ẩm thực', 'Du lịch', 'Y tế', 'Thời trang', 'Khác'
    ];

    useEffect(() => {
        console.log("AOS simulated or replaced by CSS animations");
    }, []);

    const handlePageSelect = (e) => {
        const pageId = e.target.value;
        const selectedPage = myPages.find(p => p._id === pageId);

        if (selectedPage) {
            setFormData({
                ...formData,
                page_id: pageId,
                title: selectedPage.name,
                description: selectedPage.description || '',
                demo_url: selectedPage.url || ''
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Kiểm tra validation tối thiểu
        if (!formData.page_id || !formData.title || !formData.description || !formData.price) {
            alert('⚠️ Vui lòng điền đầy đủ thông tin bắt buộc');
            return;
        }

        // Logic submit (Giả lập axios post)
        setSubmitting(true);
        console.log('Payload:', { ...formData, price: parseFloat(formData.price) });

        setTimeout(() => {
            alert(testMode ? '✅ Đăng bán thành công! (Test Mode)' : '✅ Đăng bán thành công! Đang chờ admin duyệt.');
            setSubmitting(false);
        }, 2000);
    };

    // Tính toán số tiền nhận được
    const calculatedReceiveAmount = formData.price ? (parseFloat(formData.price) * 0.9) : 0;

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="sell-page-container">
            <Header />
            <div className="sell-page-main">
                {/* TRUYỀN userRole VÀO SIDEBAR */}
                <Sidebar userRole={userRole} />
                <div className="sell-page-content">
                    <div className="sell-page-header">
                        <h1>Đăng bán Landing Page</h1>
                        <p>Đăng bán landing page của bạn lên Marketplace chuyên nghiệp</p>
                    </div>

                    {testMode && (
                        <div className="test-mode-banner">
                            <AlertCircle size={20} color="var(--color-warning-dark)" />
                            <div>
                                <strong>🧪 Test Mode:</strong> Tất cả dữ liệu là demo. Form hoạt động đầy đủ nhưng không lưu thật.
                            </div>
                        </div>
                    )}

                    {/* START: Cấu trúc 2 cột mới */}
                    <div className="form-layout-grid">

                        {/* CỘT 1: FORM CHÍNH */}
                        <div className="sell-form-column">
                            <form onSubmit={handleSubmit} className="sell-form">
                                {/* Select Landing Page */}
                                <div className="form-group">
                                    <label>
                                        <FileText size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Chọn Landing Page
                                        <span className="required">*</span>
                                        <Tooltip text="Chọn landing page từ danh sách của bạn">
                                            <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                        </Tooltip>
                                    </label>
                                    <select
                                        value={formData.page_id}
                                        onChange={handlePageSelect}
                                        required
                                    >
                                        <option value="">-- Chọn landing page (có thể chưa publish) --</option>
                                        {myPages.map(page => (
                                            <option key={page._id} value={page._id}>
                                                {page.name} ({page.status === 'ĐÃ XUẤT BẢN' ? 'Đã Xuất Bản' : 'Chưa Xuất Bản'})
                                            </option>
                                        ))}
                                    </select>
                                    {myPages.length === 0 && (
                                        <p className="help-text">
                                            Bạn chưa có landing page nào. <a href="/pages">Tạo ngay</a>
                                        </p>
                                    )}
                                    <div className="feature-badge">
                                        <CheckCircle size={14} />
                                        Không cần publish để bán
                                    </div>
                                </div>

                                {/* Title */}
                                <div className="form-group">
                                    <label>
                                        <Edit3 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Tiêu đề
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.title}
                                        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                        placeholder="Nhập tiêu đề hấp dẫn cho landing page"
                                        maxLength={200}
                                        required
                                    />
                                    <span className="char-count">{formData.title.length}/200</span>
                                </div>

                                {/* Description */}
                                <div className="form-group">
                                    <label>
                                        <List size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Mô tả
                                        <span className="required">*</span>
                                    </label>
                                    <textarea
                                        value={formData.description}
                                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                        placeholder="Mô tả chi tiết về landing page của bạn, tính năng, ưu điểm..."
                                        maxLength={2000}
                                        rows={6}
                                        required
                                    />
                                    <span className="char-count">{formData.description.length}/2000</span>
                                </div>

                                {/* Category */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Danh mục
                                        <span className="required">*</span>
                                    </label>
                                    <select
                                        value={formData.category}
                                        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                        required
                                    >
                                        {categories.map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Pricing */}
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                            Giá bán (VNĐ)
                                            <span className="required">*</span>
                                            <Tooltip text="Platform thu phí 10%">
                                                <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                            </Tooltip>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.price}
                                            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                            placeholder="500000"
                                            min="0"
                                            step="1000"
                                            required
                                        />
                                        <p className="help-text">
                                            <TrendingUp size={14} style={{ display: 'inline', marginRight: '4px' }} />
                                            Platform sẽ thu phí 10%. Bạn nhận: <strong>{calculatedReceiveAmount.toLocaleString('vi-VN')} VNĐ</strong>
                                        </p>
                                    </div>

                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-text-secondary)" className="label-icon" />
                                            Giá gốc (VNĐ)
                                            <span className="optional">(Tùy chọn)</span>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.original_price}
                                            onChange={(e) => setFormData({ ...formData, original_price: e.target.value })}
                                            placeholder="800000"
                                            min="0"
                                            step="1000"
                                        />
                                        <p className="help-text">Hiển thị % giảm giá</p>
                                    </div>
                                </div>

                                {/* Tags */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Tags
                                        <span className="optional">(Tùy chọn)</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.tags}
                                        onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                                        placeholder="VD: responsive, modern, ecommerce (cách nhau bằng dấu phẩy)"
                                    />
                                </div>

                                {/* Demo URL */}
                                <div className="form-group">
                                    <label>
                                        <Link2 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        URL Demo
                                        <span className="optional">(Tùy chọn)</span>
                                    </label>
                                    <input
                                        type="url"
                                        value={formData.demo_url}
                                        onChange={(e) => setFormData({ ...formData, demo_url: e.target.value })}
                                        placeholder="https://example.com/demo"
                                    />
                                </div>

                                {/* Submit Button (Đặt bên dưới form chính) */}
                                <div className="form-actions-full">
                                    <button
                                        type="button"
                                        className="btn-secondary"
                                        onClick={() => console.log('Navigate to /pages')}
                                        disabled={submitting}
                                    >
                                        Hủy
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn-primary"
                                        disabled={submitting}
                                    >
                                        {submitting ? '⏳ Đang xử lý...' : <>🚀 Đăng bán ngay</>}
                                    </button>
                                </div>
                            </form>
                        </div>

                        {/* CỘT 2: INFO BOX */}
                        <div className="info-box-column">
                            <div className="info-box">
                                <h3>
                                    <Info size={18} />
                                    Lưu ý quan trọng
                                </h3>
                                <ul>
                                    <li>Không cần publish - Bán ngay sau khi tạo nội dung</li>
                                    <li>Hệ thống tự động copy images & screenshot</li>
                                    <li>Admin review trong <Clock size={14} style={{ display: 'inline', margin: '0 4px', verticalAlign: 'middle' }} /><strong>24-48 giờ</strong></li>
                                    <li>Đảm bảo không vi phạm bản quyền</li>
                                    <li>Platform thu phí <strong>10%</strong> mỗi giao dịch</li>
                                    <li>Có thể cập nhật thông tin sau khi đăng</li>
                                </ul>
                                <div className="info-box-feature">
                                    <Shield size={20} color="var(--color-success-money)" />
                                    <div>
                                        <strong>Bảo vệ người mua:</strong> Hoàn tiền 100% nếu sản phẩm không đúng mô tả
                                    </div>
                                </div>
                            </div>
                            <div className="test-mode-section">
                                <div className="test-mode-header">
                                    <AlertCircle size={18} color="var(--color-warning-dark)" />
                                    <strong>Test Mode</strong>
                                </div>
                                <button
                                    onClick={() => setTestMode(!testMode)}
                                    className={`test-mode-button ${testMode ? 'active' : 'inactive'}`}
                                >
                                    {testMode ? '✓ Test Mode ON' : 'Bật Test Mode'}
                                </button>
                                <p className="test-mode-note">
                                    Bật để test form với dữ liệu demo
                                </p>
                            </div>
                        </div>
                    </div>
                    {/* END: Cấu trúc 2 cột mới */}
                </div>
            </div>
        </div>
    );
};

export default SellPage;
