import React, { useState, useEffect, useContext } from 'react';
// Import c√°c icon t·ª´ lucide-react
import {
    FileText, Edit3, List, Tag, DollarSign,
    Link2, Info, HelpCircle, CheckCircle,
    AlertCircle, TrendingUp, Shield, Clock
} from 'lucide-react';
// Import CSS
import '../styles/SellPage.css';

// Mock contexts v√† components (B·∫°n s·∫Ω thay th·∫ø b·∫±ng import th·∫≠t)
const UserContext = React.createContext({ user: { role: 'seller' } });
const Header = () => <div style={{ height: '60px', background: '#192A56', color: 'white', display: 'flex', alignItems: 'center', padding: '0 20px', fontSize: '1.2rem', fontWeight: '600' }}>Marketplace Platform</div>;

// Component Sidebar M·ªöI ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t
const Sidebar = ({ userRole }) => (
    <div style={{ width: '250px', background: '#F8F9FB', borderRight: '1px solid #EAECEE', padding: '20px' }}>
        Sidebar for {userRole}
    </div>
);

const DogLoader = () => <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2rem' }}>‚è≥ Loading...</div>;

// --- TOOLTIP COMPONENT ---
const Tooltip = ({ children, text }) => {
    const [show, setShow] = useState(false);
    return (
        <div className="tooltip-container">
            <div
                onMouseEnter={() => setShow(true)}
                onMouseLeave={() => setShow(false)}
                className="tooltip-target"
            >
                {children}
            </div>
            {show && (
                <div className="tooltip-content">
                    {text}
                </div>
            )}
        </div>
    );
};

const SellPage = () => {
    // Gi·∫£ l·∫≠p c√°c state c·∫ßn thi·∫øt
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(false); // ƒê·∫∑t l·∫°i l√† false cho demo
    // L·∫•y userRole t·ª´ Context ho·∫∑c gi·∫£ l·∫≠p m·∫∑c ƒë·ªãnh l√† 'seller'
    const [userRole, setUserRole] = useState(user?.role || 'seller');
    const [submitting, setSubmitting] = useState(false);
    const [testMode, setTestMode] = useState(true); // Th√™m state Test Mode
    const [myPages] = useState([
        { _id: '1', name: 'Landing Page Pro', status: 'ƒê√É XU·∫§T B·∫¢N', description: 'Professional landing page', url: 'https://example.com/pro-demo' },
        { _id: '2', name: 'E-commerce Store', status: 'NH√ÅP', description: 'Modern store template', url: 'https://example.com/store-demo' }
    ]);
    const [formData, setFormData] = useState({
        page_id: '',
        title: '',
        description: '',
        category: 'Landing Page',
        price: '',
        original_price: '',
        tags: '',
        demo_url: ''
    });

    // Gi·∫£ l·∫≠p categories
    const categories = [
        'Landing Page', 'Th∆∞∆°ng m·∫°i m·∫°i ƒëi·ªán t·ª≠', 'Blog', 'Portfolio',
        'Doanh nghi·ªáp', 'Gi√°o d·ª•c', 'S·ª± ki·ªán', 'B·∫•t ƒë·ªông s·∫£n',
        '·∫®m th·ª±c', 'Du l·ªãch', 'Y t·∫ø', 'Th·ªùi trang', 'Kh√°c'
    ];

    useEffect(() => {
        // Trong m√¥i tr∆∞·ªùng React th·ª±c, b·∫°n s·∫Ω import v√† g·ªçi AOS.init ·ªü ƒë√¢y
        console.log("AOS simulated or replaced by CSS animations");
    }, []);

    const handlePageSelect = (e) => {
        const pageId = e.target.value;
        const selectedPage = myPages.find(p => p._id === pageId);

        if (selectedPage) {
            setFormData({
                ...formData,
                page_id: pageId,
                title: selectedPage.name,
                description: selectedPage.description || '',
                demo_url: selectedPage.url || ''
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Ki·ªÉm tra validation t·ªëi thi·ªÉu
        if (!formData.page_id || !formData.title || !formData.description || !formData.price) {
            alert('‚ö†Ô∏è Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc');
            return;
        }

        // Logic submit (Gi·∫£ l·∫≠p axios post)
        setSubmitting(true);
        console.log('Payload:', { ...formData, price: parseFloat(formData.price) });

        setTimeout(() => {
            alert(testMode ? '‚úÖ ƒêƒÉng b√°n th√†nh c√¥ng! (Test Mode)' : '‚úÖ ƒêƒÉng b√°n th√†nh c√¥ng! ƒêang ch·ªù admin duy·ªát.');
            setSubmitting(false);
        }, 2000);
    };

    // T√≠nh to√°n s·ªë ti·ªÅn nh·∫≠n ƒë∆∞·ª£c
    const calculatedReceiveAmount = formData.price ? (parseFloat(formData.price) * 0.9) : 0;

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="sell-page-container">
            <Header />
            <div className="sell-page-main">
                {/* TRUY·ªÄN userRole V√ÄO SIDEBAR */}
                <Sidebar userRole={userRole} />
                <div className="sell-page-content">
                    <div className="sell-page-header">
                        <h1>ƒêƒÉng b√°n Landing Page</h1>
                        <p>ƒêƒÉng b√°n landing page c·ªßa b·∫°n l√™n Marketplace chuy√™n nghi·ªáp</p>
                    </div>

                    {testMode && (
                        <div className="test-mode-banner">
                            <AlertCircle size={20} color="var(--color-warning-dark)" />
                            <div>
                                <strong>üß™ Test Mode:</strong> T·∫•t c·∫£ d·ªØ li·ªáu l√† demo. Form ho·∫°t ƒë·ªông ƒë·∫ßy ƒë·ªß nh∆∞ng kh√¥ng l∆∞u th·∫≠t.
                            </div>
                        </div>
                    )}

                    {/* START: C·∫•u tr√∫c 2 c·ªôt m·ªõi */}
                    <div className="form-layout-grid">

                        {/* C·ªòT 1: FORM CH√çNH */}
                        <div className="sell-form-column">
                            <form onSubmit={handleSubmit} className="sell-form">
                                {/* Select Landing Page */}
                                <div className="form-group">
                                    <label>
                                        <FileText size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Ch·ªçn Landing Page
                                        <span className="required">*</span>
                                        <Tooltip text="Ch·ªçn landing page t·ª´ danh s√°ch c·ªßa b·∫°n">
                                            <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                        </Tooltip>
                                    </label>
                                    <select
                                        value={formData.page_id}
                                        onChange={handlePageSelect}
                                        required
                                    >
                                        <option value="">-- Ch·ªçn landing page (c√≥ th·ªÉ ch∆∞a publish) --</option>
                                        {myPages.map(page => (
                                            <option key={page._id} value={page._id}>
                                                {page.name} ({page.status === 'ƒê√É XU·∫§T B·∫¢N' ? 'ƒê√£ Xu·∫•t B·∫£n' : 'Ch∆∞a Xu·∫•t B·∫£n'})
                                            </option>
                                        ))}
                                    </select>
                                    {myPages.length === 0 && (
                                        <p className="help-text">
                                            B·∫°n ch∆∞a c√≥ landing page n√†o. <a href="/pages">T·∫°o ngay</a>
                                        </p>
                                    )}
                                    <div className="feature-badge">
                                        <CheckCircle size={14} />
                                        Kh√¥ng c·∫ßn publish ƒë·ªÉ b√°n
                                    </div>
                                </div>

                                {/* Title */}
                                <div className="form-group">
                                    <label>
                                        <Edit3 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Ti√™u ƒë·ªÅ
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.title}
                                        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                        placeholder="Nh·∫≠p ti√™u ƒë·ªÅ h·∫•p d·∫´n cho landing page"
                                        maxLength={200}
                                        required
                                    />
                                    <span className="char-count">{formData.title.length}/200</span>
                                </div>

                                {/* Description */}
                                <div className="form-group">
                                    <label>
                                        <List size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        M√¥ t·∫£
                                        <span className="required">*</span>
                                    </label>
                                    <textarea
                                        value={formData.description}
                                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                        placeholder="M√¥ t·∫£ chi ti·∫øt v·ªÅ landing page c·ªßa b·∫°n, t√≠nh nƒÉng, ∆∞u ƒëi·ªÉm..."
                                        maxLength={2000}
                                        rows={6}
                                        required
                                    />
                                    <span className="char-count">{formData.description.length}/2000</span>
                                </div>

                                {/* Category */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Danh m·ª•c
                                        <span className="required">*</span>
                                    </label>
                                    <select
                                        value={formData.category}
                                        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                        required
                                    >
                                        {categories.map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Pricing */}
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                            Gi√° b√°n (VNƒê)
                                            <span className="required">*</span>
                                            <Tooltip text="Platform thu ph√≠ 10%">
                                                <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                            </Tooltip>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.price}
                                            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                            placeholder="500000"
                                            min="0"
                                            step="1000"
                                            required
                                        />
                                        <p className="help-text">
                                            <TrendingUp size={14} style={{ display: 'inline', marginRight: '4px' }} />
                                            Platform s·∫Ω thu ph√≠ 10%. B·∫°n nh·∫≠n: <strong>{calculatedReceiveAmount.toLocaleString('vi-VN')} VNƒê</strong>
                                        </p>
                                    </div>

                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-text-secondary)" className="label-icon" />
                                            Gi√° g·ªëc (VNƒê)
                                            <span className="optional">(T√πy ch·ªçn)</span>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.original_price}
                                            onChange={(e) => setFormData({ ...formData, original_price: e.target.value })}
                                            placeholder="800000"
                                            min="0"
                                            step="1000"
                                        />
                                        <p className="help-text">Hi·ªÉn th·ªã % gi·∫£m gi√°</p>
                                    </div>
                                </div>

                                {/* Tags */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Tags
                                        <span className="optional">(T√πy ch·ªçn)</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.tags}
                                        onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                                        placeholder="VD: responsive, modern, ecommerce (c√°ch nhau b·∫±ng d·∫•u ph·∫©y)"
                                    />
                                </div>

                                {/* Demo URL */}
                                <div className="form-group">
                                    <label>
                                        <Link2 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        URL Demo
                                        <span className="optional">(T√πy ch·ªçn)</span>
                                    </label>
                                    <input
                                        type="url"
                                        value={formData.demo_url}
                                        onChange={(e) => setFormData({ ...formData, demo_url: e.target.value })}
                                        placeholder="https://example.com/demo"
                                    />
                                </div>

                                {/* Submit Button (ƒê·∫∑t b√™n d∆∞·ªõi form ch√≠nh) */}
                                <div className="form-actions-full">
                                    <button
                                        type="button"
                                        className="btn-secondary"
                                        onClick={() => console.log('Navigate to /pages')}
                                        disabled={submitting}
                                    >
                                        H·ªßy
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn-primary"
