import React, { useRef, useEffect, useCallback, useMemo } from 'react';
import PropTypes from 'prop-types';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import SectionToolkit from './toolkitqick/SectionToolkit';
import { ItemTypes, getCanvasPosition, snapToGrid, getElementBounds } from './helpers';
import eventController from '../../utils/EventUtils';
import { getResponsiveValues } from '../../utils/responsiveSync';
import { Type } from '../../constants/elements'; // Import Type từ elements.js

/**
 * Reusable utility to generate locked styles for elements
 */
const getLockedStyles = (isLocked) =>
    useMemo(() => (isLocked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {}), [isLocked]);

/**
 * Get canvas width based on view mode
 */
const getCanvasWidth = (viewMode) => {
    switch (viewMode) {
        case 'mobile':
            return 375;
        case 'tablet':
            return 768;
        case 'desktop':
        default:
            return 1200;
    }
};

/**
 * Utility to generate CSS for icon component
 */
const toKebabCase = (str) => str.replace(/([A-Z])/g, '-$1').toLowerCase();

const getIconCSS = (styles, uniqueClass, hasImage) => {
    const iconStyles = { ...styles };
    const imgCSS = hasImage
        ? `
        .${uniqueClass} img {
            width: 100%;
            height: 100%;
            object-fit: contain;
        }
        `
        : '';

    return `
        .${uniqueClass} {
            ${Object.entries(iconStyles)
        .map(([key, value]) => `${toKebabCase(key)}: ${value};`)
        .join(' ')}
        }
        .${uniqueClass} svg {
            width: 100%;
            height: 100%;
            fill: none;
            stroke: ${iconStyles.color || 'currentColor'};
            stroke-width: ${iconStyles.strokeWidth || 2};
            stroke-linecap: ${iconStyles.strokeLinecap || 'round'};
            stroke-linejoin: ${iconStyles.strokeLinejoin || 'round'};
        }
        ${imgCSS}
    `;
};

/**
 * ChildElement component for rendering draggable child elements within sections or popups
 */
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         position,
         size,
         viewMode,
         visible = true,
         locked = false,
         onDeleteChild,
         element,
     }) => {
        const dragRef = useRef(null);

        // Get responsive values for current view mode
        const { size: responsiveSize, position: responsivePosition, styles: responsiveStyles } = useMemo(() => {
            if (element) {
                return getResponsiveValues(element, viewMode);
            }
            return {
                size: size || { width: 200, height: 50 },
                position: position || { x: 0, y: 0 },
                styles: styles || {},
            };
        }, [element, viewMode, size, position, styles]);

        const [{ isDragging }, drag] = useDrag({
            type: ItemTypes.CHILD_ELEMENT,
            canDrag: () => !locked && !componentData.locked,
            item: () => {
                if (locked || componentData.locked) {
                    toast.warning('Child element đã bị khóa!');
                    return null;
                }
                return { childId: id, parentId, isExisting: true, position: responsivePosition, size: responsiveSize };
            },
            collect: (monitor) => ({ isDragging: monitor.isDragging() }),
        }, [id, parentId, responsivePosition, responsiveSize, locked, componentData.locked]);

        const handleClick = useCallback(
            (e) => {
                console.log(`ChildElement ${id} (type: ${type}) clicked, parentId: ${parentId}`); // Debug
                if (typeof onSelectChild === 'function') {
                    onSelectChild(parentId, id);
                    console.log(`onSelectChild called with parentId: ${parentId}, id: ${id}`); // Debug
                }
                if (componentData.events?.onClick) {
                    eventController.handleEvent(componentData.events.onClick, id, true);
                }
            },
            [parentId, id, type, onSelectChild, componentData.events]
        );

        const handleDelete = useCallback(
            (e) => {
                e.stopPropagation();
                e.preventDefault();
                if (typeof onDeleteChild === 'function' && !locked && !componentData.locked) {
                    onDeleteChild(parentId, id, e);
                    toast.success('Đã xóa thành phần con!');
                } else {
                    toast.warn('Không thể xóa: Element bị khóa!');
                }
            },
            [parentId, id, onDeleteChild, locked, componentData.locked]
        );

        useEffect(() => {
            if (!locked && !componentData.locked) {
                drag(dragRef);
            }
        }, [drag, locked, componentData.locked]);

        if (!visible || componentData.visible === false) {
            return null;
        }

        const lockedStyles = getLockedStyles(locked || componentData.locked);

        // Calculate width based on view mode for specific element types
        const getElementWidth = () => {
            if (type === Type.GALLERY) return 380;
            if (type === Type.ICON) return 50;
            if (type === Type.HEADING || type === Type.PARAGRAPH) return responsiveSize.width || 'auto';
            if (type === Type.IMAGE) return responsiveSize.width || 200;
            if (type === Type.BUTTON) return responsiveSize.width || 150;
            if (type === Type.VIDEO) return responsiveSize.width || 320;
            return responsiveSize.width || 200;
        };

        const getElementHeight = () => {
            if (type === Type.GALLERY) return 300;
            if (type === Type.ICON) return 50;
            if (type === Type.HEADING || type === Type.PARAGRAPH) return responsiveSize.height || 'auto';
            if (type === Type.IMAGE) return responsiveSize.height || 200;
            if (type === Type.BUTTON) return responsiveSize.height || 40;
            if (type === Type.VIDEO) return responsiveSize.height || 180;
            return responsiveSize.height || 50;
        };

        // Outer wrapper styles - CHỈ positioning
        const wrapperStyles = useMemo(
            () => ({
                position: 'absolute',
                left: responsivePosition.x || 0,
                top: responsivePosition.y || 0,
                width: getElementWidth(),
                height: getElementHeight(),
                zIndex: isDragging ? 1000 : responsivePosition.z || 20,
                cursor: locked || componentData.locked ? 'not-allowed' : isDragging ? 'grabbing' : 'pointer',
                opacity: isDragging ? 0.5 : 1,
                pointerEvents: 'auto',
                ...lockedStyles,
            }),
            [responsivePosition, type, isDragging, locked, componentData.locked, lockedStyles]
        );

        // Content styles - Đảm bảo văn bản có thể click
        const contentStyles = useMemo(
            () => ({
                width: '100%',
                height: '100%',
                pointerEvents: 'auto',
                userSelect: type === Type.HEADING || type === Type.PARAGRAPH ? 'text' : 'none',
                ...responsiveStyles,
            }),
            [type, responsiveStyles]
        );

        return (
            <div
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${
                    isDragging ? 'lpb-child-element-dragging' : ''
                } ${locked || componentData.locked ? 'lpb-element-locked' : ''}`}
                style={wrapperStyles}
                onClick={handleClick}
            >
                <div
                    ref={dragRef}
                    style={{
                        width: '100%',
                        height: '100%',
                        position: 'relative',
                        pointerEvents: 'auto',
                    }}
                >
                    {renderComponentContent(
                        type, // Sử dụng type thay vì element.type
                        componentData,
                        contentStyles,
                        element.children || [],
                        true,
                        onSelectChild,
                        id,
                        null,
                        false,
                        viewMode
                    )}
                </div>
                {(locked || componentData.locked) && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '2px',
                            right: '2px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '2px 6px',
                            borderRadius: '3px',
                            fontSize: '10px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock" />
                    </div>
                )}
                {isSelected && (
                    <button
                        onClick={handleDelete}
                        style={{
                            position: 'absolute',
                            top: '2px',
                            left: '2px',
                            zIndex: 10002,
                            borderRadius: '50%',
                            background: '#ff7b7b',
                            border: 'none',
                            color: '#ffffff',
                            fontSize: '16px',
                            cursor: 'pointer',
                            padding: '6px',
                            width: '30px',
                            height: '30px',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                        }}
                    >
                        <i className="fas fa-trash-alt" />
                    </button>
                )}
            </div>
        );
    }
);

/**
 * Element component for rendering sections, popups, or other canvas elements
 */
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
         visibleElements = [],
         onCloseElement,
         onSaveTemplate,
         onToggleVisibility,
         onEditElement,
         onMoveElementUp,
         onMoveElementDown,
         onDeleteElement,
         onDeleteChild,
     }) => {
        const {
            id,
            type,
            componentData = {},
            children = [],
            visible = true,
            locked = false,
        } = element;

        // Get responsive values for current view mode
        const { size: responsiveSize, position: responsivePosition, styles: responsiveStyles } = useMemo(
            () => getResponsiveValues(element, viewMode),
            [element, viewMode]
        );

        const sectionStyles = useMemo(
            () => ({
                position: 'absolute',
                top: responsivePosition.y || 0,
                width: `${getCanvasWidth(viewMode)}px`,
                height: responsiveSize.height,
                left: '50%',
                transform: 'translateX(-50%)',
                zIndex: responsivePosition.z || 1, // Sử dụng zIndex từ responsivePosition
                userSelect: 'none',
                overflow: 'visible',
                ...responsiveStyles,
                background: componentData.backgroundType === 'gradient'
                    ? (responsiveStyles.background || componentData.background || 'none')
                    : componentData.backgroundColor || responsiveStyles.backgroundColor || 'transparent',
                backgroundImage: componentData.backgroundType === 'image' && componentData.backgroundImage
                    ? `url(${componentData.backgroundImage})`
                    : 'none',
                backgroundSize: responsiveStyles.backgroundSize || componentData.backgroundSize || 'cover',
                backgroundPosition: responsiveStyles.backgroundPosition || componentData.backgroundPosition || 'center',
                backgroundRepeat: responsiveStyles.backgroundRepeat || componentData.backgroundRepeat || 'no-repeat',
            }),
            [responsivePosition, responsiveSize, viewMode, componentData, responsiveStyles]
        );

        const canvasWidth = getCanvasWidth(viewMode);

        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const [{ isDragging }, drag, preview] = useDrag({
            type: ItemTypes.EXISTING_ELEMENT,
            canDrag: () => !locked && type !== Type.SECTION && type !== Type.POPUP,
            item: () => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return null;
                }
                return {
                    id,
                    elementSize: responsiveSize,
                    elementPosition: responsivePosition,
                    isExisting: true,
                };
            },
            collect: (monitor) => ({ isDragging: monitor.isDragging() }),
        }, [id, responsiveSize, responsivePosition, locked, type]);

        const snapPoints = useMemo(
            () => {
                const points = [
                    { x: 0, y: 0 },
                    { x: responsiveSize.width, y: responsiveSize.height },
                    { x: responsiveSize.width / 2, y: responsiveSize.height / 2 },
                ];
                children.forEach((child) => {
                    const childResponsive = getResponsiveValues(child, viewMode);
                    const bounds = {
                        left: childResponsive.position.x,
                        top: childResponsive.position.y,
                        right: childResponsive.position.x + childResponsive.size.width,
                        bottom: childResponsive.position.y + childResponsive.size.height,
                        centerX: childResponsive.position.x + childResponsive.size.width / 2,
                        centerY: childResponsive.position.y + childResponsive.size.height / 2,
                    };
                    points.push(
                        { x: bounds.left, y: bounds.top },
                        { x: bounds.right, y: bounds.bottom },
                        { x: bounds.centerX, y: bounds.centerY },
                    );
                });
                return points;
            },
            [responsiveSize, children, viewMode]
        );

        const [{ isOverContainer, canDropContainer }, dropSection] = useDrop({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: (item, monitor) => {
                if (type !== Type.SECTION || componentData?.structure !== 'ladi-standard') return false;
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) return false;
                return true;
            },
            drop: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return { moved: false };
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };
                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.id}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success('Đã thêm thành phần con vào section!');
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    if (item.parentId === id) {
                        onUpdateChildPosition(id, item.childId, snapped);
                        toast.success('Đã di chuyển thành phần con trong section!');
                    } else {
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success('Đã di chuyển thành phần con sang section khác!');
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverContainer: monitor.isOver(),
                canDropContainer: monitor.canDrop(),
            }),
        }, [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

        const [{ isOverPopup, canDropPopup }, dropPopup] = useDrop({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: () => type === Type.POPUP,
            drop: (item, monitor) => {
                if (!containerRef.current) return { moved: false };
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };
                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.id}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success('Đã thêm thành phần con vào popup!');
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    if (item.parentId === id) {
                        onUpdateChildPosition(id, item.childId, snapped);
                        toast.success('Đã di chuyển thành phần con trong popup!');
                    } else {
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success('Đã di chuyển thành phần con sang popup khác!');
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverPopup: monitor.isOver(),
                canDropPopup: monitor.canDrop(),
            }),
        }, [id, type, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

        const lockedStyles = getLockedStyles(locked);

        const animationStyles = useMemo(
            () => ({
                animation: componentData.animation?.onLoad
                    ? `${componentData.animation.onLoad} ${componentData.animation?.duration || 1000}ms ease`
                    : componentData.animation?.type && type !== Type.GALLERY
                        ? `${componentData.animation.type} ${componentData.animation?.duration || 1000}ms ease ${componentData.animation?.delay || 0}ms ${componentData.animation?.repeat ? 'infinite' : ''}`
                        : 'none',
            }),
            [componentData.animation, type]
        );

        const popupStyles = useMemo(
            () => ({
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                width: viewMode === 'mobile' ? '90%' : responsiveSize.width || 600,
                maxWidth: viewMode === 'mobile' ? '90%' : '600px',
                minHeight: responsiveSize.height || 400,
                maxHeight: '90vh',
                zIndex: 1001,
                background: componentData.background || 'rgba(255, 255, 255, 0.95)',
                borderRadius: componentData.borderRadius || '12px',
                boxShadow: isSelected ? '0 20px 60px rgba(0, 0, 0, 0.3), 0 0 0 3px #3b82f6' : '0 8px 32px rgba(0, 0, 0, 0.25)',
                overflow: 'hidden',
                display: 'flex',
                flexDirection: 'column',
                transition: 'all 0.3s ease, opacity 0.3s ease, transform 0.3s ease',
                opacity: 1,
                transformOrigin: 'center',
                cursor: locked ? 'not-allowed' : 'default',
                ...responsiveStyles,
                ...lockedStyles,
                ...animationStyles,
            }),
            [viewMode, responsiveSize, componentData, isSelected, responsiveStyles, lockedStyles, animationStyles]
        );

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        useEffect(() => {
            if (type === Type.SECTION && componentData?.structure === 'ladi-standard') {
                dropSection(containerRef);
            } else if (type === Type.POPUP) {
                dropPopup(containerRef);
            }
        }, [dropSection, dropPopup, type, componentData]);

        const handleMouseDown = useCallback(
            (e) => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return;
                }
                if (!e.target.closest('button')) {
                    e.stopPropagation();
                    if (typeof onSelectElement === 'function') {
                        onSelectElement([id], e.ctrlKey);
                    }
                    onSelectChild(id, null);
                    console.log(`Element ${id} selected`); // Debug
                }
            },
            [id, locked, onSelectElement, onSelectChild]
        );

        const handleClick = useCallback(
            (e) => {
                e.stopPropagation();
                if (componentData.events?.onClick) {
                    eventController.handleEvent(componentData.events.onClick, id, true);
                }
            },
            [id, componentData.events]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        if (!visible) {
            return null;
        }

        const isPopup = type === Type.POPUP;
        const shouldShowElement = type === Type.SECTION || isSelected || visibleElements.includes(id);
        if (isPopup && !shouldShowElement) {
            return null;
        }

        if (type === Type.SECTION && componentData?.structure === 'ladi-standard') {
            return (
                <div
                    ref={elementRef}
                    data-element-id={id}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                    style={sectionStyles}
                    onMouseDown={locked ? undefined : handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {locked && (
                        <div
                            style={{
                                position: 'absolute',
                                top: '-18px',
                                right: '-18px',
                                zIndex: 10001,
                                background: 'rgba(0,0,0,0.7)',
                                color: '#fff',
                                padding: '6px 12px',
                                borderRadius: '6px',
                                fontSize: '12px',
                                pointerEvents: 'none',
                            }}
                        >
                            <i className="fas fa-lock" /> Section Locked
                        </div>
                    )}
                    <div
                        className="ladi-section"
                        style={{
                            width: '100%',
                            height: '100%',
                            position: 'relative',
                            isolation: 'isolate',
                        }}
                    >
                        <div
                            className="ladi-section-background"
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 0,
                                pointerEvents: 'none',
                                backgroundColor: componentData.backgroundType === 'color'
                                    ? (componentData.backgroundColor || responsiveStyles.backgroundColor || 'transparent')
                                    : 'transparent',
                                backgroundImage: componentData.backgroundType === 'image' && componentData.backgroundImage
                                    ? `url(${componentData.backgroundImage})`
                                    : 'none',
                                backgroundSize: responsiveStyles.backgroundSize || componentData.backgroundSize || 'cover',
                                backgroundPosition: responsiveStyles.backgroundPosition || componentData.backgroundPosition || 'center',
                                backgroundRepeat: responsiveStyles.backgroundRepeat || componentData.backgroundRepeat || 'no-repeat',
                                backgroundAttachment: responsiveStyles.backgroundAttachment || componentData.backgroundAttachment || 'scroll',
                                background: componentData.backgroundType === 'gradient'
                                    ? (componentData.gradient || responsiveStyles.background || 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)')
                                    : undefined,
                            }}
                        />
                        <div
                            className="ladi-overlay"
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 1,
                                pointerEvents: 'none',
                                backgroundColor: componentData.overlayColor || responsiveStyles.overlayColor || 'transparent',
                                opacity: componentData.overlayOpacity !== undefined
                                    ? componentData.overlayOpacity
                                    : (responsiveStyles.overlayOpacity || 0),
                            }}
                        />
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOverContainer && canDropContainer ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 10,
                                padding: componentData.padding || responsiveStyles.padding || '20px',
                                width: '100%',
                                height: '100%',
                                border: isOverContainer && canDropContainer ? '2px dashed #2563eb' : 'none',
                                pointerEvents: 'auto',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    element={child}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>
                    </div>
                    {isSelected && (
                        <SectionToolkit
                            element={element}
                            position={{ x: 0, y: 0 }}
                            onEdit={() => onEditElement(id)}
                            onSaveTemplate={onSaveTemplate}
                            onMoveUp={() => onMoveElementUp(id)}
                            onMoveDown={() => onMoveElementDown(id)}
                            onToggleVisibility={() => onToggleVisibility(id)}
                            onDelete={() => onDeleteElement(id)}
                        />
                    )}
                </div>
            );
        }

        if (type === Type.POPUP) {
            return (
                <>
                    <div
                        className="lpb-popup-backdrop"
                        style={{
                            position: 'fixed',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.5)',
                            zIndex: 1000,
                            pointerEvents: shouldShowElement ? 'auto' : 'none',
                            opacity: shouldShowElement ? 1 : 0,
                            transition: 'opacity 0.3s ease',
                        }}
                        onClick={(e) => {
                            e.stopPropagation();
                            if (typeof onCloseElement === 'function') {
                                onCloseElement(id);
                                eventController.dispatch('element-close', { elementId: id });
                            }
                        }}
                    />
                    <div
                        ref={elementRef}
                        data-element-id={id}
                        className={`lpb-popup-container ${isSelected ? 'lpb-popup-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                        style={popupStyles}
                        onMouseDown={locked ? undefined : handleMouseDown}
                        onContextMenu={handleContextMenu}
                    >
                        <div
                            className="lpb-popup-header"
                            style={{
                                padding: '16px 20px',
                                borderBottom: '1px solid #e5e7eb',
                                background: 'linear-gradient(to bottom, #f9fafb, #ffffff)',
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                flexShrink: 0,
                            }}
                        >
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#111827' }}>
                                {componentData.title || 'Popup Title'}
                            </h3>
                            <button
                                className="lpb-popup-close"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    if (typeof onCloseElement === 'function') {
                                        onCloseElement(id);
                                        eventController.dispatch('element-close', { elementId: id });
                                    }
                                }}
                                style={{
                                    width: '28px',
                                    height: '28px',
                                    border: 'none',
                                    background: 'transparent',
                                    cursor: 'pointer',
                                    borderRadius: '6px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    transition: 'all 0.15s ease',
                                    color: '#6b7280',
                                }}
                            >
                                <i className="fas fa-times" style={{ fontSize: '14px' }} />
                            </button>
                        </div>
                        <div
                            ref={containerRef}
                            className={`lpb-popup-content ${isOverPopup && canDropPopup ? 'lpb-popup-dropzone-active' : ''}`}
                            style={{
                                position: 'relative',
                                flex: 1,
                                padding: componentData.padding || '20px',
                                overflowY: 'auto',
                                overflowX: 'hidden',
                                minHeight: '200px',
                                border: isOverPopup && canDropPopup ? '2px dashed #2563eb' : 'none',
                                background: isOverPopup && canDropPopup ? 'rgba(37, 99, 235, 0.05)' : 'transparent',
                                transition: 'all 0.2s ease',
                            }}
                        >
                            {children.length === 0 && (
                                <div
                                    className="lpb-popup-empty"
                                    style={{
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        minHeight: '200px',
                                        color: '#9ca3af',
                                        textAlign: 'center',
                                        padding: '40px 20px',
                                    }}
                                >
                                    <i
                                        className="fas fa-hand-pointer"
                                        style={{ fontSize: '48px', marginBottom: '16px', opacity: 0.3, color: '#d1d5db' }}
                                    />
                                    <p style={{ margin: '0 0 8px', fontSize: '14px', fontWeight: '500', color: '#6b7280' }}>
                                        Kéo thả elements vào đây
                                    </p>
                                    <small style={{ fontSize: '12px', color: '#9ca3af' }}>
                                        Popup có thể chứa bất kỳ element nào
                                    </small>
                                </div>
                            )}
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    element={child}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>
                        {locked && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: '5px',
                                    right: '5px',
                                    zIndex: 10001,
                                    background: 'rgba(0,0,0,0.7)',
                                    color: '#fff',
                                    padding: '4px 8px',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    pointerEvents: 'none',
                                }}
                            >
                                <i className="fas fa-lock" /> Locked
                            </div>
                        )}
                    </div>
                </>
            );
        }

        // Regular elements (not section or popup)
        const commonElementStyles = useMemo(
            () => ({
                position: 'absolute',
                top: responsivePosition.y || 0,
                width: type === Type.GALLERY ? responsiveSize.width || 380 : responsiveSize.width,
                height: type === Type.GALLERY ? responsiveSize.height || 300 : responsiveSize.height,
                left: type === Type.GALLERY ? responsivePosition.x || 0 : '50%',
                transform: type !== Type.GALLERY ? 'translateX(-50%)' : 'none',
                zIndex: responsivePosition.z || 1,
                opacity: isDragging ? 0.5 : 1,
                cursor: locked ? 'not-allowed' : isDragging ? 'grabbing' : 'pointer',
                userSelect: type === Type.HEADING || type === Type.PARAGRAPH ? 'text' : 'none',
                ...responsiveStyles,
                ...lockedStyles,
                ...animationStyles,
            }),
            [responsivePosition, responsiveSize, type, isDragging, locked, responsiveStyles, lockedStyles, animationStyles]
        );

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    if (!locked && type !== Type.SECTION && type !== Type.POPUP) {
                        drag(node);
                    }
                }}
                data-element-id={id}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                style={commonElementStyles}
                onMouseDown={locked ? undefined : handleMouseDown}
                onContextMenu={handleContextMenu}
                onClick={handleClick}
            >
                {locked && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '5px',
                            right: '5px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '11px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock" /> Locked
                    </div>
                )}
                {renderComponentContent(
                    type,
                    componentData,
                    responsiveStyles,
                    children,
                    true,
                    onSelectChild,
                    id,
                    null,
                    false,
                    viewMode
                )}
            </div>
        );
    }
);

// PropTypes for ChildElement
ChildElement.propTypes = {
    parentId: PropTypes.string.isRequired,
    id: PropTypes.string.isRequired,
    type: PropTypes.string.isRequired,
    element: PropTypes.object,
    componentData: PropTypes.shape({
        locked: PropTypes.bool,
        visible: PropTypes.bool,
        events: PropTypes.shape({
            onClick: PropTypes.object,
        }),
        images: PropTypes.arrayOf(PropTypes.string),
    }),
    styles: PropTypes.object,
    isSelected: PropTypes.bool.isRequired,
    onSelectChild: PropTypes.func.isRequired,
    position: PropTypes.object.isRequired,
    size: PropTypes.shape({
        width: PropTypes.number,
        height: PropTypes.number,
    }),
    viewMode: PropTypes.oneOf(['desktop', 'tablet', 'mobile']).isRequired,
    visible: PropTypes.bool,
    locked: PropTypes.bool,
    onDeleteChild: PropTypes.func.isRequired,
};

// PropTypes for Element
Element.propTypes = {
    element: PropTypes.shape({
        id: PropTypes.string.isRequired,
        type: PropTypes.string.isRequired,
        componentData: PropTypes.object,
        position: PropTypes.object,
        size: PropTypes.object,
        styles: PropTypes.object,
        children: PropTypes.array,
        visible: PropTypes.bool,
        locked: PropTypes.bool,
    }).isRequired,
    isSelected: PropTypes.bool.isRequired,
    onSelectElement: PropTypes.func.isRequired,
    onUpdatePosition: PropTypes.func.isRequired,
    viewMode: PropTypes.oneOf(['desktop', 'tablet', 'mobile']).isRequired,
    zoomLevel: PropTypes.number.isRequired,
    gridSize: PropTypes.number.isRequired,
    onContextMenu: PropTypes.func.isRequired,
    canvasBounds: PropTypes.shape({
        width: PropTypes.number,
        height: PropTypes.number,
    }).isRequired,
    canvasRef: PropTypes.shape({ current: PropTypes.instanceOf(Element) }).isRequired,
    onSelectChild: PropTypes.func.isRequired,
    selectedChildId: PropTypes.string,
    onAddChild: PropTypes.func.isRequired,
    onUpdateChildPosition: PropTypes.func.isRequired,
    onMoveChild: PropTypes.func.isRequired,
    showGrid: PropTypes.bool.isRequired,
    setDragPreview: PropTypes.func.isRequired,
    visibleElements: PropTypes.arrayOf(PropTypes.string),
    onCloseElement: PropTypes.func.isRequired,
    onSaveTemplate: PropTypes.func.isRequired,
    onToggleVisibility: PropTypes.func.isRequired,
    onEditElement: PropTypes.func.isRequired,
    onMoveElementUp: PropTypes.func.isRequired,
    onMoveElementDown: PropTypes.func.isRequired,
    onDeleteElement: PropTypes.func.isRequired,
    onDeleteChild: PropTypes.func.isRequired,
};

// Hàm renderComponentContent được sửa để hỗ trợ thêm image, button, video và tránh lỗi cú pháp
export const renderComponentContent = (
    elementType,
    componentData = {},
    styles = {},
    children = [],
    isCanvas,
    onSelectChild,
    parentId,
    childId,
    isTemplateMode,
    viewMode
) => {
    console.log(`Rendering elementType: ${elementType}, parentId: ${parentId}, childId: ${childId}`); // Debug

    // Đảm bảo văn bản có thể click
    const textStyles = {
        pointerEvents: 'auto',
        userSelect: 'text',
        cursor: 'pointer',
        ...styles,
    };

    const mediaStyles = {
        pointerEvents: 'auto',
        cursor: 'pointer',
        ...styles,
    };

    // Kiểm tra styles để debug
    if (!styles || Object.keys(styles).length === 0) {
        console.warn(`Warning: styles is empty for ${elementType}, parentId: ${parentId}, childId: ${childId}`);
    }

    switch (elementType) {
        case Type.HEADING:
            return (
                <h1 style={textStyles} data-element-id={childId}>
                    {componentData.content || 'Default Heading'}
                </h1>
            );
        case Type.PARAGRAPH:
            return (
                <p style={textStyles} data-element-id={childId}>
                    {componentData.content || 'Default Paragraph'}
                </p>
            );
        case Type.GALLERY:
            return (
                <div
                    style={{
                        ...mediaStyles,
                        display: styles.display || 'grid',
                        gridTemplateColumns: styles.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))',
                        gap: styles.gap || '10px',
                        width: '100%',
                        height: '100%',
                        borderRadius: styles.borderRadius || '8px',
                        overflow: styles.overflow || 'hidden',
                    }}
                >
                    {componentData.images && componentData.images.length > 0 ? (
                        componentData.images.map((imageUrl, index) => (
                            <div
                                key={index}
                                style={{
                                    position: 'relative',
                                    overflow: 'hidden',
                                    borderRadius: styles.borderRadius || '8px',
                                    aspectRatio: '1 / 1',
                                    cursor: componentData.events?.onClick ? 'pointer' : 'default',
                                }}
                                onClick={(e) => {
                                    e.stopPropagation();
                                    if (isCanvas && typeof onSelectChild === 'function' && parentId && childId) {
                                        onSelectChild(parentId, childId);
                                    }
                                    if (componentData.events?.onClick) {
                                        eventController.handleEvent(componentData.events.onClick, childId || parentId, isCanvas);
                                    }
                                }}
                            >
                                <img
                                    src={imageUrl || 'https://via.placeholder.com/150'}
                                    alt={`Gallery ${index + 1}`}
                                    style={{
                                        width: '100%',
                                        height: '100%',
                                        objectFit: 'cover',
                                    }}
                                    onError={(e) => {
                                        e.target.src = 'https://via.placeholder.com/150';
                                        console.warn(`Failed to load gallery image: ${imageUrl}`);
                                    }}
                                    loading="lazy"
                                />
                            </div>
                        ))
                    ) : (
                        <img
                            src="https://via.placeholder.com/380x300?text=Gallery"
                            alt="Gallery Placeholder"
                            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                        />
                    )}
                </div>
            );
        case Type.ICON: {
            const { icon, events = {}, title, imageUrl } = componentData;
            const uniqueClass = `icon-${parentId || 'root'}-${childId || Date.now()}`;
            const isSvg = icon && icon.startsWith('<svg');

            const handleClick = (e) => {
                e.stopPropagation();
                if (isCanvas && typeof onSelectChild === 'function' && parentId && childId) {
                    onSelectChild(parentId, childId);
                }
                if (events.onClick) {
                    eventController.handleEvent(events.onClick, childId || parentId, isCanvas);
                }
            };

            const iconStyles = {
                ...styles,
                width: componentData.size?.width || styles.width || '50px',
                height: componentData.size?.height || styles.height || '50px',
                display: styles.display || 'flex',
                alignItems: styles.alignItems || 'center',
                justifyContent: styles.justifyContent || 'center',
                color: styles.color || componentData.color || 'inherit',
                cursor: events.onClick ? 'pointer' : 'default',
                position: styles.position || 'absolute',
                zIndex: styles.zIndex || 10,
            };

            const cssString = getIconCSS(styles, uniqueClass, !!imageUrl);

            return (
                <>
                    {cssString && <style>{cssString}</style>}
                    <div
                        className={`lpb-icon ${uniqueClass}`}
                        style={iconStyles}
                        onClick={handleClick}
                        title={title || ''}
                    >
                        {imageUrl ? (
                            <img
                                src={imageUrl}
                                alt={title || 'Icon'}
                                style={{
                                    width: '100%',
                                    height: '100%',
                                    objectFit: 'contain',
                                    color: iconStyles.color,
                                }}
                                onError={(e) => {
                                    e.target.src = 'https://via.placeholder.com/50?text=Icon';
                                    console.warn(`Failed to load icon image: ${imageUrl}`);
                                }}
                            />
                        ) : isSvg ? (
                            <div
                                dangerouslySetInnerHTML={{ __html: icon }}
                                style={{
                                    width: '100%',
                                    height: '100%',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    color: iconStyles.color,
                                }}
                            />
                        ) : (
                            icon && <i className={icon} style={{ fontSize: 'inherit', color: 'inherit' }} />
                        )}
                    </div>
                </>
            );
        }
        case Type.IMAGE:
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/200?text=Image'}
                    alt={componentData.alt || 'Image'}
                    style={mediaStyles}
                />
            );
        case Type.BUTTON:
            return (
                <button
                    style={{
                        padding: componentData.padding || '10px 20px',
                        borderRadius: componentData.borderRadius || '4px',
                        background: componentData.background || '#007bff',
                        color: componentData.color || '#fff',
                        border: 'none',
                        cursor: 'pointer',
                        ...styles,
                        pointerEvents: 'auto',
                    }}
                    onClick={(e) => {
                        e.stopPropagation();
                        if (componentData.events?.onClick) {
                            eventController.handleEvent(componentData.events.onClick, childId || parentId, isCanvas);
                        }
                    }}
                >
                    {componentData.content || 'Button'}
                </button>
            );
        case Type.VIDEO:
            return (
                <video
                    controls
                    src={componentData.src || 'https://www.w3schools.com/html/mov_bbb.mp4'}
                    style={mediaStyles}
                >
                    Your browser does not support the video tag.
                </video>
            );
        case Type.SECTION:
            return (
                <div className="ladi-section" style={{ position: 'relative', width: '100%', height: '100%', overflow: 'visible' }}>
                    <div className="ladi-container" style={{ position: 'relative', zIndex: 2, width: '100%', height: '100%' }}>
                        {children.map((child, index) => {
                            if (!child.type || !child.id) {
                                console.warn('Invalid child:', child);
                                return null;
                            }
                            return (
                                <ChildElement
                                    key={child.id}
                                    parentId={parentId}
                                    id={child.id}
                                    type={child.type}
                                    element={child}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={childId === child.id}
                                    onSelectChild={onSelectChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            );
                        })}
                    </div>
                </div>
            );
        default:
            return <div style={styles}>Unsupported element type: {elementType}</div>;
    }
};

export { ChildElement, Element };
