import React, { useState, useEffect, useContext, useMemo } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/UnifiedPayments.css';
import DogLoader from '../components/Loader';
import PayoutRequest from '../components/PayoutRequest';
import {
    DollarSign, TrendingUp, CheckCircle, Clock, Filter,
    Download, Calendar, CreditCard, Eye, RotateCcw
} from 'lucide-react';

const UnifiedPayments = () => {
    /* ---------- context & router ---------- */
    const { user } = useContext(UserContext);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
    const [showPayoutModal, setShowPayoutModal] = useState(false);
    /* ---------- state ---------- */
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);        // 'admin' | 'user'
    const [stats, setStats] = useState(null);              // object
    const [transactions, setTransactions] = useState([]);  // array
    const [totalPages, setTotalPages] = useState(1);
    const [page, setPage] = useState(1);

    const [filters, setFilters] = useState({
        status: 'all',
        paymentMethod: 'all',
        startDate: '',
        endDate: ''
    });

    /* ---------- auth ---------- */
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) { navigate('/auth'); return; }
        try {
            const { role } = jwtDecode(token);
            setUserRole(role);
        } catch {
            navigate('/auth');
        } finally {
            setLoading(false);
        }
    }, [navigate]);

    /* ---------- animate ---------- */
    useEffect(() => { AOS.init({ duration: 600, once: true }); }, []);

    /* ---------- fetch data ---------- */
    useEffect(() => {
        if (!userRole) return;
        loadStats();
        loadTransactions();
    }, [userRole, filters, page]);

    const loadStats = async () => {
        try {
            const token = localStorage.getItem('token');
            const url = userRole === 'admin'
                ? `${API_BASE_URL}/api/payment/admin/stats`
                : `${API_BASE_URL}/api/payment/stats`;
            console.log('Fetching stats from:', url); // Log URL
            const { data } = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            setStats(data.data ?? null);
        } catch (error) {
            console.error('Error fetching stats:', error); // Log chi ti·∫øt l·ªói
            toast.error('Kh√¥ng t·∫£i ƒë∆∞·ª£c th·ªëng k√™');
            setStats(null);
        }
    };

    const loadTransactions = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const params = new URLSearchParams({ page, limit: 20, ...filters });
            const url = userRole === 'admin'
                ? `${API_BASE_URL}/api/payment/admin/transactions?${params}`
                : `${API_BASE_URL}/api/payment/transactions?${params}`;
            console.log('Fetching transactions from:', url); // Log URL
            const { data } = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            const txData = userRole === 'admin' ? data.data.transactions : data.data;
            setTransactions(Array.isArray(txData) ? txData : []);
            setTotalPages(data.data.pagination?.totalPages || 1);
        } catch (error) {
            console.error('Error fetching transactions:', error.response?.status, error.response?.data); // Log chi ti·∫øt
            toast.error(`Kh√¥ng t·∫£i ƒë∆∞·ª£c giao d·ªãch: ${error.response?.data?.message || error.message}`);
            setTransactions([]);
            setTotalPages(1);
        } finally {
            setLoading(false);
        }
    };

    /* ---------- helpers ---------- */
    const formatPrice = (n) =>
        new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(n);

    const formatDate = (d) => new Date(d).toLocaleString('vi-VN');

    /* ---------- export csv ---------- */
    const exportCSV = () => {
        const headers = ['ID', 'Ng√†y', 'S·∫£n ph·∫©m', 'Ph∆∞∆°ng th·ª©c', 'S·ªë ti·ªÅn', 'Tr·∫°ng th√°i'];
        if (userRole === 'admin') headers.splice(3, 0, 'Ng∆∞·ªùi mua', 'Ng∆∞·ªùi b√°n');

        const rows = transactions.map((tx) => {
            const base = [
                tx._id.slice(-6),
                formatDate(tx.created_at),
                tx.marketplace_page_id?.title || '-',
                tx.payment_method,
                formatPrice(tx.amount),
                tx.status
            ];
            if (userRole === 'admin') {
                base.splice(3, 0,
                    tx.buyer_id?.name || tx.buyer_id?.email || '-',
                    tx.seller_id?.name || tx.seller_id?.email || '-'
                );
            }
            return base;
        });

        const csv = [headers, ...rows].map((r) => r.join(',')).join('\n');
        const blob = new Blob(['\uFEFF' + csv], { type: 'text/csv;charset=utf-8;' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `transactions_${new Date().toISOString().slice(0, 10)}.csv`;
        a.click();
        URL.revokeObjectURL(url);
    };

    /* ---------- clear filter ---------- */
    const resetFilter = () => {
        setFilters({ status: 'all', paymentMethod: 'all', startDate: '', endDate: '' });
        setPage(1);
    };

    /* ---------- render ---------- */
    if (loading && !stats && !transactions.length) return <DogLoader />;

    const completed = stats?.completedCount ?? 0;
    const pending   = stats?.pendingCount   ?? 0;

    return (
        <div className="unified-payments">
            <Sidebar role={userRole} />
            <div className="main-area">
                <Header />
                <div className="content">
                    {/* page header */}
                    <div className="page-header" data-aos="fade-down">
                        <h1>{userRole === 'admin' ? 'üí≥ Qu·∫£n l√Ω giao d·ªãch' : 'üí∞ L·ªãch s·ª≠ thanh to√°n'}</h1>
                        <p>{userRole === 'admin' ? 'To√†n b·ªô giao d·ªãch & l·ª£i nhu·∫≠n' : 'Giao d·ªãch c·ªßa b·∫°n'}</p>
                    </div>

                    {/* stats */}
                    {stats && (
                        <div className="stats-grid" data-aos="fade-up">
                            <div className="stat-card">
                                <DollarSign size={28} />
                                <div>
                                    <div className="val">{formatPrice(stats.totalRevenue || 0)}</div>
                                    <div className="lbl">{userRole === 'admin' ? 'T·ªïng doanh thu' : 'Doanh thu b√°n'}</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <TrendingUp size={28} />
                                <div>
                                    <div className="val">{formatPrice(stats.totalPlatformFee || stats.totalEarned || 0)}</div>
                                    <div className="lbl">{userRole === 'admin' ? 'L·ª£i nhu·∫≠n' : 'Thu nh·∫≠p th·ª±c'}</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <CheckCircle size={28} />
                                <div>
                                    <div className="val">{completed}</div>
                                    <div className="lbl">Th√†nh c√¥ng</div>
                                </div>
                            </div>
                            <div className="stat-card">
                                <Clock size={28} />
                                <div>
                                    <div className="val">{pending}</div>
                                    <div className="lbl">Ch·ªù x·ª≠ l√Ω</div>
                                </div>
                            </div>
                        </div>
                    )}
                    {stats && userRole !== 'admin' && (
                        <div className="stat-card highlight-card" onClick={() => {
                            if (stats.pendingPayout > 0) {
                                setShowPayoutModal(true);
                            } else {
                                toast.info('B·∫°n ch∆∞a c√≥ ti·ªÅn ƒë·ªÉ r√∫t');
                            }
                        }}>
                            <div className="stat-icon" style={{ background: '#fef3c7' }}>
                                <Clock size={28} color="#d97706" />
                            </div>
                            <div className="stat-info">
                                <div className="stat-value">{formatPrice(stats.pendingPayout || 0)}</div>
                                <div className="stat-label">Ch·ªù r√∫t ti·ªÅn</div>
                                <div className="stat-sublabel">Click ƒë·ªÉ y√™u c·∫ßu r√∫t</div>
                            </div>
                        </div>
                    )}

                    {/* filters */}
                    <div className="filters" data-aos="fade-up">
                        <select value={filters.status} onChange={(e) => setFilters({ ...filters, status: e.target.value })}>
                            <option value="all">T·∫•t c·∫£ tr·∫°ng th√°i</option>
                            <option value="COMPLETED">Ho√†n th√†nh</option>
                            <option value="PENDING">Ch·ªù</option>
                            <option value="FAILED">Th·∫•t b·∫°i</option>
                        </select>
                        <select value={filters.paymentMethod} onChange={(e) => setFilters({ ...filters, paymentMethod: e.target.value })}>
                            <option value="all">T·∫•t c·∫£ ph∆∞∆°ng th·ª©c</option>
                            <option value="MOMO">MOMO</option>
                            <option value="VNPAY">VNPay</option>
                            <option value="SANDBOX">Sandbox</option>
                        </select>
                        <input
                            type="date"
                            value={filters.startDate}
                            onChange={(e) => setFilters({ ...filters, startDate: e.target.value })}
                        />
                        <input
                            type="date"
                            value={filters.endDate}
                            onChange={(e) => setFilters({ ...filters, endDate: e.target.value })}
                        />
                        <button onClick={loadTransactions}>
                            <Filter size={16} /> L·ªçc
                        </button>
                        <button onClick={exportCSV}>
                            <Download size={16} /> Xu·∫•t
                        </button>
                        <button onClick={resetFilter} className="reset">
                            <RotateCcw size={16} /> Reset
                        </button>
                    </div>

                    {/* table */}
                    <div className="table-wrapper" data-aos="fade-up">
                        {loading ? (
                            <DogLoader />
                        ) : !transactions.length ? (
                            <p className="empty">Kh√¥ng c√≥ giao d·ªãch</p>
                        ) : (
                            <>
                                <table>
                                    <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Ng√†y</th>
                                        <th>S·∫£n ph·∫©m</th>
                                        {userRole === 'admin' && <th>Ng∆∞·ªùi mua</th>}
                                        {userRole === 'admin' && <th>Ng∆∞·ªùi b√°n</th>}
                                        <th>Ph∆∞∆°ng th·ª©c</th>
                                        <th>S·ªë ti·ªÅn</th>
                                        <th>Tr·∫°ng th√°i</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {transactions.map((tx) => (
                                        <tr key={tx._id}>
                                            <td>
                                                <code>{tx._id.slice(-6)}</code>
                                            </td>
                                            <td>{formatDate(tx.created_at)}</td>
                                            <td>{tx.marketplace_page_id?.title || '-'}</td>
                                            {userRole === 'admin' && <td>{tx.buyer_id?.name || tx.buyer_id?.email || '-'}</td>}
                                            {userRole === 'admin' && <td>{tx.seller_id?.name || tx.seller_id?.email || '-'}</td>}
                                            <td>{tx.payment_method}</td>
                                            <td>
                                                <strong>{formatPrice(tx.amount)}</strong>
                                            </td>
                                            <td>
                                                <span className={`badge1 ${tx.status.toLowerCase()}`}>{tx.status}</span>
                                            </td>
                                        </tr>
                                    ))}
                                    </tbody>
                                </table>

                                {totalPages > 1 && (
                                    <div className="pagination">
                                        <button disabled={page === 1} onClick={() => setPage((p) => p - 1)}>
                                            Prev
                                        </button>
                                        <span>
                      Page {page} / {totalPages}
                    </span>
                                        <button disabled={page === totalPages} onClick={() => setPage((p) => p + 1)}>
                                            Next
                                        </button>
                                    </div>
                                )}
                            </>
                        )}
                    </div>
                </div>
            </div>
            {showPayoutModal && (
                <PayoutRequest
                    isOpen={showPayoutModal}
                    onClose={() => setShowPayoutModal(false)}
                    pendingAmount={stats.pendingPayout || 0}
                    onSuccess={() => {
                        toast.success('Y√™u c·∫ßu r√∫t ti·ªÅn ƒë√£ g·ª≠i!');
                        loadStats(); // reload s·ªë d∆∞
                    }}
                />
            )}
        </div>
    );
};

export default UnifiedPayments;
