import React, { useState, useEffect, useContext } from 'react';
// Import các icon từ lucide-react (Đã được giữ lại cho Tooltip và Info Box trong SellPage)
import {
    FileText, Edit3, List, Tag, DollarSign,
    Link2, Info, HelpCircle, CheckCircle,
    AlertCircle, TrendingUp, Shield, Clock
} from 'lucide-react';
// Import CSS (Giả định bạn có cả '../styles/SellPage.css' và '../styles/doashboard-sidebar.css')
import '../styles/SellPage.css';
import '../styles/doashboard-sidebar.css'; // Cần thiết cho Sidebar
import '../styles/header.css'; // Cần thiết cho Header

// ---------------------------------------------------
// START: MOCK DEPENDENCIES (Thực tế bạn sẽ import)
// ---------------------------------------------------
const UserContext = React.createContext({ user: { role: 'seller' } });
const DogLoader = () => <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2rem' }}>⏳ Loading...</div>;

// MOCK: Giả lập react-router-dom
const useLocation = () => ({ pathname: '/sell-page' }); // Giả lập đang ở trang /sell-page
const useNavigate = () => (path) => console.log('Navigate to:', path);
const Link = ({ to, children, className, title, onClick }) => (
    <a href={to} className={className} title={title} onClick={(e) => { e.preventDefault(); onClick && onClick(); console.log('Link clicked:', to); }}>
        {children}
    </a>
);

// MOCK: Giả lập API và jwtDecode
const jwtDecode = (token) => ({ userId: '123', role: 'seller' });
const api = { get: async () => ({ data: { name: 'Seller User', avatar: '' } }) };
const logo = 'URL_TO_YOUR_LOGO';
const UserProfile = () => <div style={{ padding: '10px', border: '1px solid #ccc' }}>User Profile Info</div>;
// ---------------------------------------------------
// END: MOCK DEPENDENCIES
// ---------------------------------------------------

// ---------------------------------------------------
// START: HEADER COMPONENT (Đã được tích hợp)
// ---------------------------------------------------
const Header = () => {
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Giả lập API call
                // const res = await api.get('/api/user/info');
                // setUserData(res.data);
                setUserData({ name: 'Mock Seller', avatar: null });
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/auth';
    };

    const displayName = userData.name || (localStorage.getItem('token') ? `User ${jwtDecode(localStorage.getItem('token')).userId}` : 'Khách');

    const [showProfile, setShowProfile] = useState(false);

    if (error) return <div style={{ color: 'red' }}>{error}</div>;
    // Giả lập token để hiển thị UI
    const tokenExists = true; // Giả lập token tồn tại

    return (
        <div className="header-container">
            <nav className="navbar">
                <div className="navbar-container">
                    <img
                        src={logo}
                        alt="Logo"
                        className="logo-image"
                    />
                    <div className="user-info">
                        {tokenExists ? (
                            <>
                                <span>Chào, {displayName}</span>
                                <div
                                    className="user-avatar cursor-pointer flex items-center justify-center rounded-full bg-gray-100 hover:bg-gray-200"
                                    onClick={() => setShowProfile(!showProfile)}
                                    style={{ width: "34px", height: "34px" }}
                                >
                                    {userData.avatar ? (
                                        <img
                                            src={userData.avatar}
                                            alt="User Avatar"
                                            className="w-full h-full rounded-full object-cover"
                                        />
                                    ) : (
                                        <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            viewBox="0 0 24 24"
                                            fill="none"
                                            stroke="currentColor"
                                            strokeWidth="1.8"
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            className="w-5 h-5 text-gray-600"
                                        >
                                            <circle cx="12" cy="8" r="4" />
                                            <path d="M4 20c0-3.5 4-5.5 8-5.5s8 2 8 5.5" />
                                        </svg>
                                    )}
                                </div>
                                <button onClick={handleLogout}>Đăng xuất</button>
                                {showProfile && (
                                    <div className="profile-popup">
                                        <UserProfile />
                                        <button onClick={() => setShowProfile(false)}>Đóng</button>
                                    </div>
                                )}
                            </>
                        ) : (
                            <button onClick={() => navigate('/auth')}>Đăng nhập</button>
                        )}
                    </div>
                </div>
            </nav>
        </div>
    )
};
// ---------------------------------------------------
// END: HEADER COMPONENT
// ---------------------------------------------------


// ---------------------------------------------------
// START: SIDEBAR COMPONENT (Đã được tích hợp)
// ---------------------------------------------------
const Sidebar = ({ role }) => {
    const [isCompact, setIsCompact] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const saved = localStorage.getItem("sidebarCompact");
        if (saved === "true") {
            setIsCompact(true);
        }
    }, []);

    useEffect(() => {
        if (location.pathname === "/pages") {
            setIsCompact(true);
            localStorage.setItem("sidebarCompact", "true");
        } else {
            setIsCompact(false); // Đặt lại thành false nếu không phải /pages để Sidebar mở
            localStorage.setItem("sidebarCompact", "false");
        }
    }, [location.pathname]);

    const userMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Landing Page', path: '/pages', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
            )},
        { name: 'Kho template', path: '/templates', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )},
        { name: 'Marketplace', path: '/marketplace', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
            )},
        { name: 'Đăng bán', path: '/sell-page', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
            )},
        { name: 'Quản lý bán hàng', path: '/my-sales', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
            )},
        { name: 'Form Data', path: '/setting-form', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )},
        { name: 'Thanh toán', path: '/payments', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Lịch sử rút tiền', path: '/my-payouts', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
            )}
    ];

    const adminMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Quản lý Tài khoản', path: '/users', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
            )},
        { name: 'Duyệt Marketplace', path: '/admin/marketplace', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
            )},
        { name: 'Kiểm tra Thanh toán', path: '/payments', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Quản lý rút tiền', path: '/admin/payouts', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
            )},
        { name: 'Template Inconic', path: '/qltemplates', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )},
        { name: 'Báo cáo Hệ thống', path: '/reports', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className={`sidebar ${isCompact ? 'compact' : ''}`}>
            {/* Background pattern */}
            <div className="sidebar-bg-pattern"></div>

            {/* Header */}
            <div className="sidebar-header">
                {!isCompact && (
                    <div className="sidebar-brand">
                        <div className="sidebar-brand-icon">
                            <svg fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
                            </svg>
                        </div>
                        <span className="sidebar-brand-text">Dashboard</span>
                    </div>
                )}
                <button className="sidebar-toggle" onClick={() => setIsCompact(!isCompact)}>
                    <svg className={`sidebar-toggle-icon ${isCompact ? 'rotated' : ''}`}
                         fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                </button>
            </div>

            {/* Menu Items */}
            <nav className="sidebar-nav">
                {menu.map((item) => {
                    // Cần giả lập location.pathname cho đúng trang hiện tại
                    const isActive = location.pathname === item.path;
                    return (
                        <Link
                            key={item.name}
                            to={item.path}
                            className={`sidebar-item ${isActive ? 'active' : ''} ${isCompact ? 'compact' : ''}`}
                            title={isCompact ? item.name : ''}
                            onClick={() => {
                                if (item.path === "/pages") {
                                    setIsCompact(true);
                                    localStorage.setItem("sidebarCompact", "true");

                                }
                            }}
                        >
                            {isActive && <div className="sidebar-item-indicator"></div>}

                            <div className="sidebar-icon">
                                {item.icon}
                            </div>

                            {!isCompact && (
                                <span className="sidebar-text">{item.name}</span>
                            )}

                            <div className="sidebar-item-glow"></div>
                        </Link>
                    );
                })}
            </nav>

            {/* Footer */}
            {!isCompact && (
                <div className="sidebar-footer">
                    <div className="sidebar-status">
                        <div className="sidebar-status-icon">
                            <div className="sidebar-status-dot"></div>
                        </div>
                        <span className="sidebar-status-text">System Online</span>
                    </div>
                </div>
            )}
        </aside>
    );
};
// ---------------------------------------------------
// END: SIDEBAR COMPONENT
// ---------------------------------------------------


// --- TOOLTIP COMPONENT --- (Giữ nguyên)
const Tooltip = ({ children, text }) => {
    const [show, setShow] = useState(false);
    return (
        <div className="tooltip-container">
            <div
                onMouseEnter={() => setShow(true)}
                onMouseLeave={() => setShow(false)}
                className="tooltip-target"
            >
                {children}
            </div>
            {show && (
                <div className="tooltip-content">
                    {text}
                </div>
            )}
        </div>
    );
};

// --- SELL PAGE MAIN COMPONENT ---
const SellPage = () => {
    // Giả lập các state cần thiết
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(false);
    const [userRole, setUserRole] = useState(user?.role || 'seller');
    const [submitting, setSubmitting] = useState(false);
    const [testMode, setTestMode] = useState(true);
    const [myPages] = useState([
        { _id: '1', name: 'Landing Page Pro', status: 'ĐÃ XUẤT BẢN', description: 'Professional landing page', url: 'https://example.com/pro-demo' },
        { _id: '2', name: 'E-commerce Store', status: 'NHÁP', description: 'Modern store template', url: 'https://example.com/store-demo' }
    ]);
    const [formData, setFormData] = useState({
        page_id: '',
        title: '',
        description: '',
        category: 'Landing Page',
        price: '',
        original_price: '',
        tags: '',
        demo_url: ''
    });

    // Giả lập categories
    const categories = [
        'Landing Page', 'Thương mại mại điện tử', 'Blog', 'Portfolio',
        'Doanh nghiệp', 'Giáo dục', 'Sự kiện', 'Bất động sản',
        'Ẩm thực', 'Du lịch', 'Y tế', 'Thời trang', 'Khác'
    ];

    useEffect(() => {
        console.log("AOS simulated or replaced by CSS animations");
    }, []);

    const handlePageSelect = (e) => {
        const pageId = e.target.value;
        const selectedPage = myPages.find(p => p._id === pageId);

        if (selectedPage) {
            setFormData({
                ...formData,
                page_id: pageId,
                title: selectedPage.name,
                description: selectedPage.description || '',
                demo_url: selectedPage.url || ''
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Kiểm tra validation tối thiểu
        if (!formData.page_id || !formData.title || !formData.description || !formData.price) {
            alert('⚠️ Vui lòng điền đầy đủ thông tin bắt buộc');
            return;
        }

        // Logic submit (Giả lập axios post)
        setSubmitting(true);
        console.log('Payload:', { ...formData, price: parseFloat(formData.price) });

        setTimeout(() => {
            alert(testMode ? '✅ Đăng bán thành công! (Test Mode)' : '✅ Đăng bán thành công! Đang chờ admin duyệt.');
            setSubmitting(false);
        }, 2000);
    };

    // Tính toán số tiền nhận được
    const calculatedReceiveAmount = formData.price ? (parseFloat(formData.price) * 0.9) : 0;

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="sell-page-container">
            {/* Sử dụng Header thực tế */}
            <Header />
            <div className="sell-page-main">
                {/* Sử dụng Sidebar thực tế */}
                <Sidebar role={userRole} />
                <div className="sell-page-content">
                    <div className="sell-page-header">
                        <h1>Đăng bán Landing Page</h1>
                        <p>Đăng bán landing page của bạn lên Marketplace chuyên nghiệp</p>
                    </div>

                    {testMode && (
                        <div className="test-mode-banner">
                            <AlertCircle size={20} color="var(--color-warning-dark)" />
                            <div>
                                <strong>🧪 Test Mode:</strong> Tất cả dữ liệu là demo. Form hoạt động đầy đủ nhưng không lưu thật.
                            </div>
                        </div>
                    )}

                    {/* START: Cấu trúc 2 cột mới */}
                    <div className="form-layout-grid">

                        {/* CỘT 1: FORM CHÍNH */}
                        <div className="sell-form-column">
                            <form onSubmit={handleSubmit} className="sell-form">
                                {/* Select Landing Page */}
                                <div className="form-group">
                                    <label>
                                        <FileText size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Chọn Landing Page
                                        <span className="required">*</span>
                                        <Tooltip text="Chọn landing page từ danh sách của bạn">
                                            <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                        </Tooltip>
                                    </label>
                                    <select
                                        value={formData.page_id}
                                        onChange={handlePageSelect}
                                        required
                                    >
                                        <option value="">-- Chọn landing page (có thể chưa publish) --</option>
                                        {myPages.map(page => (
                                            <option key={page._id} value={page._id}>
                                                {page.name} ({page.status === 'ĐÃ XUẤT BẢN' ? 'Đã Xuất Bản' : 'Chưa Xuất Bản'})
                                            </option>
                                        ))}
                                    </select>
                                    {myPages.length === 0 && (
                                        <p className="help-text">
                                            Bạn chưa có landing page nào. <a href="/pages">Tạo ngay</a>
                                        </p>
                                    )}
                                    <div className="feature-badge">
                                        <CheckCircle size={14} />
                                        Không cần publish để bán
                                    </div>
                                </div>

                                {/* Title */}
                                <div className="form-group">
                                    <label>
                                        <Edit3 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Tiêu đề
                                        <span className="required">*</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.title}
                                        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                        placeholder="Nhập tiêu đề hấp dẫn cho landing page"
                                        maxLength={200}
                                        required
                                    />
                                    <span className="char-count">{formData.title.length}/200</span>
                                </div>

                                {/* Description */}
                                <div className="form-group">
                                    <label>
                                        <List size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Mô tả
                                        <span className="required">*</span>
                                    </label>
                                    <textarea
                                        value={formData.description}
                                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                        placeholder="Mô tả chi tiết về landing page của bạn, tính năng, ưu điểm..."
                                        maxLength={2000}
                                        rows={6}
                                        required
                                    />
                                    <span className="char-count">{formData.description.length}/2000</span>
                                </div>

                                {/* Category */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Danh mục
                                        <span className="required">*</span>
                                    </label>
                                    <select
                                        value={formData.category}
                                        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                        required
                                    >
                                        {categories.map(cat => (
                                            <option key={cat} value={cat}>{cat}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Pricing */}
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                            Giá bán (VNĐ)
                                            <span className="required">*</span>
                                            <Tooltip text="Platform thu phí 10%">
                                                <HelpCircle size={16} color="var(--color-text-secondary)" style={{ marginLeft: '8px' }} />
                                            </Tooltip>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.price}
                                            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                            placeholder="500000"
                                            min="0"
                                            step="1000"
                                            required
                                        />
                                        <p className="help-text">
                                            <TrendingUp size={14} style={{ display: 'inline', marginRight: '4px' }} />
                                            Platform sẽ thu phí 10%. Bạn nhận: <strong>{calculatedReceiveAmount.toLocaleString('vi-VN')} VNĐ</strong>
                                        </p>
                                    </div>

                                    <div className="form-group">
                                        <label>
                                            <DollarSign size={18} color="var(--color-text-secondary)" className="label-icon" />
                                            Giá gốc (VNĐ)
                                            <span className="optional">(Tùy chọn)</span>
                                        </label>
                                        <input
                                            type="number"
                                            value={formData.original_price}
                                            onChange={(e) => setFormData({ ...formData, original_price: e.target.value })}
                                            placeholder="800000"
                                            min="0"
                                            step="1000"
                                        />
                                        <p className="help-text">Hiển thị % giảm giá</p>
                                    </div>
                                </div>

                                {/* Tags */}
                                <div className="form-group">
                                    <label>
                                        <Tag size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        Tags
                                        <span className="optional">(Tùy chọn)</span>
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.tags}
                                        onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                                        placeholder="VD: responsive, modern, ecommerce (cách nhau bằng dấu phẩy)"
                                    />
                                </div>

                                {/* Demo URL */}
                                <div className="form-group">
                                    <label>
                                        <Link2 size={18} color="var(--color-accent-gold-dark)" className="label-icon" />
                                        URL Demo
                                        <span className="optional">(Tùy chọn)</span>
                                    </label>
                                    <input
                                        type="url"
                                        value={formData.demo_url}
                                        onChange={(e) => setFormData({ ...formData, demo_url: e.target.value })}
                                        placeholder="https://example.com/demo"
                                    />
                                </div>

                                {/* Submit Button (Đặt bên dưới form chính) */}
                                <div className="form-actions-full">
                                    <button
                                        type="button"
                                        className="btn-secondary"
                                        onClick={() => console.log('Navigate to /pages')}
                                        disabled={submitting}
                                    >
                                        Hủy
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn-primary"
                                        disabled={submitting}
                                    >
                                        {submitting ? '⏳ Đang xử lý...' : <>🚀 Đăng bán ngay</>}
                                    </button>
                                </div>
                            </form>
                        </div>

                        {/* CỘT 2: INFO BOX */}
                        <div className="info-box-column">
                            <div className="info-box">
                                <h3>
                                    <Info size={18} />
                                    Lưu ý quan trọng
                                </h3>
                                <ul>
                                    <li>Không cần publish - Bán ngay sau khi tạo nội dung</li>
                                    <li>Hệ thống tự động copy images & screenshot</li>
                                    <li>Admin review trong <Clock size={14} style={{ display: 'inline', margin: '0 4px', verticalAlign: 'middle' }} /><strong>24-48 giờ</strong></li>
                                    <li>Đảm bảo không vi phạm bản quyền</li>
                                    <li>Platform thu phí <strong>10%</strong> mỗi giao dịch</li>
                                    <li>Có thể cập nhật thông tin sau khi đăng</li>
                                </ul>
                                <div className="info-box-feature">
                                    <Shield size={20} color="var(--color-success-money)" />
                                    <div>
                                        <strong>Bảo vệ người mua:</strong> Hoàn tiền 100% nếu sản phẩm không đúng mô tả
                                    </div>
                                </div>
                            </div>
                            <div className="test-mode-section">
                                <div className="test-mode-header">
                                    <AlertCircle size={18} color="var(--color-warning-dark)" />
                                    <strong>Test Mode</strong>
                                </div>
                                <button
                                    onClick={() => setTestMode(!testMode)}
                                    className={`test-mode-button ${testMode ? 'active' : 'inactive'}`}
                                >
                                    {testMode ? '✓ Test Mode ON' : 'Bật Test Mode'}
                                </button>
                                <p className="test-mode-note">
                                    Bật để test form với dữ liệu demo
                                </p>
                            </div>
                        </div>
                    </div>
                    {/* END: Cấu trúc 2 cột mới */}
                </div>
            </div>
        </div>
    );
};

export default SellPage;
