import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { Search, Eye, Rocket, ImageIcon, X, AlertTriangle, CheckCircle, Star, Crown } from 'lucide-react';
import '../styles/Templates.css';
import DogLoader from '../components/Loader';

const Templates = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [templates, setTemplates] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('usage_count');
    const [usingTemplateId, setUsingTemplateId] = useState(null);
    const [previewTemplate, setPreviewTemplate] = useState(null);
    const [showPreview, setShowPreview] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const [page, setPage] = useState(1);

    const location = useLocation();
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const categories = [
        'all',
        'Thương mại điện tử',
        'Landing Page',
        'Blog',
        'Portfolio',
        'Doanh nghiệp',
        'Giáo dục',
        'Sự kiện',
        'Bất động sản',
        'Khác'
    ];
    const sortOptions = [
        { value: 'usage_count', label: 'Phổ biến nhất' },
        { value: 'created_at', label: 'Mới nhất' },
        { value: 'price_asc', label: 'Giá thấp đến cao' },
        { value: 'price_desc', label: 'Giá cao đến thấp' }
    ];

    const categoryStats = React.useMemo(() => {
        if (!templates.length) return [];
        const grouped = {};
        templates.forEach((t) => {
            grouped[t.category] = (grouped[t.category] || 0) + 1;
        });
        return Object.entries(grouped).map(([name, value]) => ({ name, value }));
    }, [templates]);

    const usageData = React.useMemo(() => {
        if (!templates.length) return [];
        return templates
            .sort((a, b) => (b.usage_count || 0) - (a.usage_count || 0))
            .slice(0, 5)
            .map((t) => ({ name: t.name.substring(0, 15), usage: t.usage_count || 0 }));
    }, [templates]);

    const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'];

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true, offset: 100 });
    }, []);

    useEffect(() => {
        if (userRole && userRole === 'user') {
            setTemplates([]);
            setPage(1);
            setHasMore(true);
            loadTemplates(1);
        }
    }, [userRole, selectedCategory, searchQuery, sortBy]);

    const loadTemplates = async (pageNum = 1) => {
        try {
            setLoading(pageNum === 1);
            const token = localStorage.getItem('token');
            const params = new URLSearchParams({
                page: pageNum,
                limit: 12,
                category: selectedCategory !== 'all' ? selectedCategory : '',
                q: searchQuery,
                sort: sortBy
            });

            const response = await axios.get(`${API_BASE_URL}/api/templates?${params}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            const newTemplates = response.data.templates || [];
            setTemplates((prev) => (pageNum === 1 ? newTemplates : [...prev, ...newTemplates]));
            setHasMore(newTemplates.length === 12);
            setError('');
        } catch (err) {
            setError('Không thể tải templates');
        } finally {
            setLoading(false);
        }
    };

    const loadMore = () => {
        if (!loading && hasMore) {
            const nextPage = page + 1;
            setPage(nextPage);
            loadTemplates(nextPage);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setTemplates([]);
        setPage(1);
        setHasMore(true);
        loadTemplates(1);
    };

    const handlePreview = async (templateId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/templates/${templateId}/preview`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPreviewTemplate(response.data);
            setShowPreview(true);
        } catch (err) {
            alert('Không thể xem trước template');
        }
    };

    const handleUseTemplate = async (templateId, templateName) => {
        if (usingTemplateId) return;
        const confirmed = window.confirm(`Tạo landing page từ "${templateName}"?`);
        if (!confirmed) return;

        try {
            setUsingTemplateId(templateId);
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_BASE_URL}/api/templates/${templateId}/use`,
                {
                    name: `${templateName} - Copy`,
                    description: `Từ template ${templateName}`
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            if (response.data.success) {
                alert('✅ Tạo thành công!');
                navigate(`/pages?id=${response.data.page.id}`);
            }
        } catch (err) {
            alert(`❌ ${err.response?.data?.error || 'Lỗi sử dụng template'}`);
        } finally {
            setUsingTemplateId(null);
        }
    };

    const getSafeImageUrl = (url) => {
        if (!url || url.startsWith('s3://')) {
            return 'https://via.placeholder.com/400x240/667eea/ffffff?text=T';
        }
        return url;
    };

    const getUniqueKey = (template, index) => `${template.id}-${index}-${page}`;

    const getPlaceholderImage = (templateName, category) => {
        const colors = {
            'Thương mại điện tử': 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'Landing Page': 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'Blog': 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
            'Portfolio': 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
            'Doanh nghiệp': 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
            'Giáo dục': 'linear-gradient(135deg, #30cfd0 0%, #330867 100%)',
            'Sự kiện': 'linear-gradient(135deg, #ff9a9e 0%, #fad0c4 100%)',
            'Bất động sản': 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'Khác': 'linear-gradient(135deg, #d4d4d4 0%, #6b7280 100%)'
        };
        return {
            background: colors[category] || colors['Khác'],
            text: templateName.charAt(0).toUpperCase()
        };
    };

    if (loading && templates.length === 0) return <DogLoader />;

    const isCompact = location.pathname === '/dashboard';

    return (
        <div className="templates-page">
            <Header role={userRole} />
            <div className="templates-main">
                <Sidebar role={userRole} isCompact={isCompact} />
                <div className="templates-content">
                    {/* Banner Quảng Cáo */}
                    <div className="banner-section" data-aos="fade-down">
                        <img
                            src="https://res.cloudinary.com/dubthm5m6/image/upload/v1760618991/Black_and_white_Geometric_Gamming_Channel_Youtube_Banner_nanfgp.jpg"
                            alt="Template Marketplace Banner"
                            className="banner-image"
                        />
                        <div className="banner-overlay">
                            <h2>Khám Phá Marketplace Templates</h2>
                            <p>200+ Mẫu Thiết Kế Chuyên Nghiệp - Nhận Ưu Đãi 20% Hôm Nay!</p>
                            <button className="banner-cta" onClick={() => navigate('/market')}>
                                Mua Ngay
                            </button>
                        </div>
                    </div>

                    {/* Stats Charts */}
                    {(categoryStats.length > 0 || usageData.length > 0) && (
                        <div className="stats-section" data-aos="fade-up">
                            <div
                                style={{
                                    display: 'grid',
                                    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                                    gap: '1.5rem',
                                    marginBottom: '2rem'
                                }}
                            >
                                {/* Category Distribution */}
                                {categoryStats.length > 0 && (
                                    <div
                                        style={{
                                            background: 'white',
                                            borderRadius: '12px',
                                            padding: '1.5rem',
                                            boxShadow: 'var(--card-shadow)'
                                        }}
                                    >
                                        <h3
                                            style={{
                                                fontSize: '0.875rem',
                                                fontWeight: '600',
                                                color: '#1e293b',
                                                marginBottom: '1rem',
                                                textTransform: 'uppercase'
                                            }}
                                        >
                                            <Star size={16} style={{ display: 'inline', marginRight: '0.5rem' }} />
                                            Phân bố theo danh mục
                                        </h3>
                                        <ResponsiveContainer width="100%" height={250}>
                                            <PieChart>
                                                <Pie
                                                    data={categoryStats}
                                                    cx="50%"
                                                    cy="50%"
                                                    labelLine={false}
                                                    label={({ name, value }) => `${name}: ${value}`}
                                                    outerRadius={80}
                                                    fill="#8884d8"
                                                    dataKey="value"
                                                >
                                                    {categoryStats.map((entry, index) => (
                                                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                                    ))}
                                                </Pie>
                                                <Tooltip />
                                            </PieChart>
                                        </ResponsiveContainer>
                                    </div>
                                )}

                                {/* Top Usage */}
                                {usageData.length > 0 && (
                                    <div
                                        style={{
                                            background: 'white',
                                            borderRadius: '12px',
                                            padding: '1.5rem',
                                            boxShadow: 'var(--card-shadow)'
                                        }}
                                    >
                                        <h3
                                            style={{
                                                fontSize: '0.875rem',
                                                fontWeight: '600',
                                                color: '#1e293b',
                                                marginBottom: '1rem',
                                                textTransform: 'uppercase'
                                            }}
                                        >
                                            <TrendingUp size={16} style={{ display: 'inline', marginRight: '0.5rem' }} />
                                            Top 5 Templates được sử dụng
                                        </h3>
                                        <ResponsiveContainer width="100%" height={250}>
                                            <BarChart data={usageData}>
                                                <CartesianGrid strokeDasharray="3 3" stroke="#e2e8f0" />
                                                <XAxis dataKey="name" stroke="#94a3b8" />
                                                <YAxis stroke="#94a3b8" />
                                                <Tooltip
                                                    contentStyle={{
                                                        background: '#1e293b',
                                                        border: 'none',
                                                        borderRadius: '8px',
                                                        color: '#fff'
                                                    }}
                                                />
                                                <Bar dataKey="usage" fill="#3b82f6" radius={[8, 8, 0, 0]} />
                                            </BarChart>
                                        </ResponsiveContainer>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {/* Search + Filters */}
                    <div className="controls-section" data-aos="fade-up">
                        <form className="search-form" onSubmit={handleSearch}>
                            <div style={{ position: 'relative', flex: 1 }}>
                                <Search
                                    size={18}
                                    style={{
                                        position: 'absolute',
                                        left: '0.75rem',
                                        top: '50%',
                                        transform: 'translateY(-50%)',
                                        color: '#94a3b8'
                                    }}
                                />
                                <input
                                    type="text"
                                    placeholder="Tìm kiếm template..."
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="search-input"
                                    style={{ paddingLeft: '2.5rem' }}
                                />
                            </div>
                            <button type="submit" className="search-btn">
                                <Search size={18} />
                            </button>
                        </form>

                        <div className="sub-filters">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="filter-select"
                            >
                                {sortOptions.map((option) => (
                                    <option key={option.value} value={option.value}>
                                        {option.label}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>

                    {/* Tabs Danh Mục */}
                    <div className="category-tabs" data-aos="fade-up">
                        {categories.map((category) => (
                            <button
                                key={category}
                                className={`tab-btn ${selectedCategory === category ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category === 'all' ? 'Tất cả' : category}
                            </button>
                        ))}
                    </div>

                    {/* Error Message */}
                    {error && (
                        <div className="error-alert" data-aos="fade-up">
                            <AlertTriangle size={24} />
                            {error}
                        </div>
                    )}

                    {/* Templates Grid */}
                    <InfiniteScroll
                        dataLength={templates.length}
                        next={loadMore}
                        hasMore={hasMore}
                        loader={<DogLoader small />}
                        endMessage={
                            <div className="end-message">
                                <CheckCircle size={48} />
                                <p>Đã tải hết templates!</p>
                            </div>
                        }
                        className="templates-grid"
                    >
                        {templates.map((template, index) => {
                            const placeholder = getPlaceholderImage(template.name, template.category);
                            const safeImage = getSafeImageUrl(template.thumbnail_url);
                            return (
                                <div key={getUniqueKey(template, index)} className="template-card" data-aos="fade-up">
                                    <div className="card-media">
                                        <img
                                            src={safeImage}
                                            alt={template.name}
                                            className="template-image"
                                            onError={(e) => {
                                                e.target.style.display = 'none';
                                                e.target.nextSibling.style.display = 'flex';
                                            }}
                                        />
                                        <div
                                            className="image-placeholder"
                                            style={{ background: placeholder.background }}
                                        >
                                            <span>{placeholder.text}</span>
                                        </div>

                                        <div className="card-overlay">
                                            <button
                                                className="btn btn-secondary"
                                                onClick={() => handlePreview(template.id)}
                                            >
                                                <Eye size={16} />
                                            </button>
                                            <button
                                                className="btn btn-primary"
                                                onClick={() => handleUseTemplate(template.id, template.name)}
                                                disabled={usingTemplateId === template.id}
                                            >
                                                {usingTemplateId === template.id ? (
                                                    <Loader size={16} className="animate-spin" />
                                                ) : (
                                                    <Rocket size={16} />
                                                )}
                                            </button>
                                        </div>

                                        {template.is_featured && (
                                            <div className="badge badge--featured">
                                                <Star size={14} /> Nổi bật
                                            </div>
                                        )}
                                        {template.is_premium && (
                                            <div className="badge badge--premium">
                                                <Crown size={14} /> {template.formatted_price}
                                            </div>
                                        )}
                                        {!template.is_premium && (
                                            <div className="badge badge--free">Free</div>
                                        )}
                                    </div>

                                    <div className="card-content">
                                        <h3 className="card-title">{template.name}</h3>
                                        <div className="card-meta">
                                            <span className="category">{template.category}</span>
                                            <span className="usage">
                                                <Eye size={14} style={{ display: 'inline', marginRight: '0.25rem' }} />{' '}
                                                {template.usage_count}
                                            </span>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </InfiniteScroll>

                    {/* Preview Modal */}
                    {showPreview && previewTemplate && (
                        <div className="modal-overlay" onClick={() => setShowPreview(false)}>
                            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                                <div className="modal-header">
                                    <h2 className="modal-title">
                                        <Eye size={20} /> {previewTemplate.template?.name}
                                    </h2>
                                    <button className="modal-close" onClick={() => setShowPreview(false)}>
                                        <X size={20} />
                                    </button>
                                </div>
                                <div className="modal-body">
                                    <iframe
                                        srcDoc={previewTemplate.html}
                                        title="Preview"
                                        className="modal-iframe"
                                    />
                                </div>
                                <div className="modal-footer">
                                    <button
                                        className="btn btn--submit"
                                        onClick={() => {
                                            setShowPreview(false);
                                            handleUseTemplate(previewTemplate.template.id, previewTemplate.template.name);
                                        }}
                                    >
                                        <Rocket size={16} /> Sử dụng
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Templates;
