/**
 * RESPONSIVE UTILS - VERSION CUỐI CÙNG ✅
 */

export const getScaleFactor = (fromMode, toMode) => {
    const widths = { desktop: 1200, tablet: 768, mobile: 375 };
    return widths[toMode] / widths[fromMode];
};

export const autoScaleSize = (element, fromMode, toMode) => {
    const scaleFactor = getScaleFactor(fromMode, toMode);

    // Section luôn full-width
    if (element.type === 'section') {
        return {
            width: toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200,
            height: element.size?.[fromMode]?.height * scaleFactor || 400
        };
    }

    // Popup có width cố định
    if (element.type === 'popup' || element.type === 'modal') {
        return {
            width: toMode === 'mobile' ? 340 : toMode === 'tablet' ? 600 : 600,
            height: element.size?.[fromMode]?.height * scaleFactor || 400
        };
    }

    // Scale các element khác - LẤY TỪ MODE SOURCE
    const baseWidth = element.size?.[fromMode]?.width || 200;
    const baseHeight = element.size?.[fromMode]?.height || 50;

    let scaledWidth = Math.round(baseWidth * scaleFactor);
    let scaledHeight = Math.round(baseHeight * scaleFactor);

    // Minimum sizes theo type
    const minSizes = {
        button: { width: 80, height: 32 },
        icon: { width: 24, height: 24 },
        image: { width: 100, height: 100 },
        heading: { width: 150, height: 24 },
        paragraph: { width: 200, height: 40 },
        gallery: { width: 300, height: 200 },
        default: { width: 100, height: 40 }
    };

    const minSize = minSizes[element.type] || minSizes.default;

    // Maximum width cho mobile
    if (toMode === 'mobile') {
        scaledWidth = Math.min(scaledWidth, 340);
    }

    return {
        width: Math.max(scaledWidth, minSize.width),
        height: Math.max(scaledHeight, minSize.height)
    };
};

export const autoScalePosition = (element, fromMode, toMode, newSize) => {
    const fromPos = element.position?.[fromMode] || { x: 0, y: 0, z: 1 };
    const scaleFactor = getScaleFactor(fromMode, toMode);

    // Section luôn x = 0
    if (element.type === 'section') {
        return { x: 0, y: 0, z: fromPos.z || 1 };
    }

    // Popup center
    if (element.type === 'popup' || element.type === 'modal') {
        const canvasW = toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200;
        return {
            x: Math.round((canvasW - (newSize?.width || 600)) / 2),
            y: fromPos.y * scaleFactor,
            z: 1001
        };
    }

    // Scale position
    let scaledX = Math.round(fromPos.x * scaleFactor);
    let scaledY = Math.round(fromPos.y * scaleFactor);

    // Đảm bảo không overflow
    const canvasWidth = toMode === 'mobile' ? 375 : toMode === 'tablet' ? 768 : 1200;
    const elementWidth = newSize?.width || 200;

    if (scaledX + elementWidth > canvasWidth) {
        scaledX = Math.max(0, canvasWidth - elementWidth - 10);
    }

    return {
        x: Math.max(0, scaledX),
        y: Math.max(0, scaledY),
        z: fromPos.z || 1
    };
};

export const autoScaleStyles = (styles, fromMode, toMode) => {
    if (!styles) return {};

    const scaleFactor = getScaleFactor(fromMode, toMode);
    const scaledStyles = { ...styles };

    const scalableProps = [
        'fontSize', 'padding', 'paddingTop', 'paddingBottom',
        'paddingLeft', 'paddingRight', 'margin', 'marginTop',
        'marginBottom', 'marginLeft', 'marginRight', 'borderRadius',
        'gap', 'borderWidth', 'strokeWidth', 'lineHeight'
    ];

    scalableProps.forEach(prop => {
        if (scaledStyles[prop]) {
            const value = scaledStyles[prop];
            if (typeof value === 'string') {
                const match = value.match(/^([\d.]+)(px|rem|em|%)$/);
                if (match) {
                    const num = parseFloat(match[1]);
                    const unit = match[2];
                    if (unit === 'px') {
                        const scaled = Math.round(num * scaleFactor * 10) / 10;
                        const min = prop.includes('font') ? 10 : 4;
                        scaledStyles[prop] = `${Math.max(scaled, min)}${unit}`;
                    }
                }
            }
        }
    });

    // Scale compound padding
    if (scaledStyles.padding && typeof scaledStyles.padding === 'string' && scaledStyles.padding.includes(' ')) {
        const values = scaledStyles.padding.split(' ');
        scaledStyles.padding = values.map(val => {
            const match = val.match(/^([\d.]+)(px|rem|em)$/);
            if (match) {
                const num = parseFloat(match[1]);
                return `${Math.max(Math.round(num * scaleFactor * 10) / 10, 4)}${match[2]}`;
            }
            return val;
        }).join(' ');
    }

    return scaledStyles;
};

export const autoScaleComponentData = (componentData, toMode) => {
    if (!componentData || toMode === 'desktop') return componentData;
    const scaled = { ...componentData };

    if (toMode === 'mobile') {
        scaled.columns = 1;
        if (scaled.images) scaled.gridTemplateColumns = '1fr';
        if (scaled.gap) scaled.gap = '8px';
    } else if (toMode === 'tablet') {
        scaled.columns = Math.min(scaled.columns || 3, 2);
        if (scaled.images) scaled.gridTemplateColumns = 'repeat(2, 1fr)';
    }

    return scaled;
};

/** ✅ MAIN SYNC - FIXED HOÀN CHỈNH */
export const syncElementBetweenModes = (element, changedMode) => {
    const modes = ['desktop', 'tablet', 'mobile'];
    const updatedElement = { ...element };

    // ✅ INIT structures
    updatedElement.size = updatedElement.size || {};
    updatedElement.position = updatedElement.position || {
        desktop: { x: 0, y: 0, z: 1 }, tablet: { x: 0, y: 0, z: 1 }, mobile: { x: 0, y: 0, z: 1 }
    };

    // ✅ SYNC size & position THEO MODE
    modes.forEach(targetMode => {
        if (targetMode !== changedMode) {
            const scaledSize = autoScaleSize(updatedElement, changedMode, targetMode);
            const scaledPosition = autoScalePosition(updatedElement, changedMode, targetMode, scaledSize);

            updatedElement.size[targetMode] = scaledSize;
            updatedElement.position[targetMode] = scaledPosition;
        }
    });

    // ✅ SYNC styles & componentData THEO MODE
    ['mobile', 'tablet'].forEach(mode => {
        updatedElement[`styles_${mode}`] = autoScaleStyles(updatedElement.styles || {}, 'desktop', mode);
        updatedElement[`componentData_${mode}`] = autoScaleComponentData(updatedElement.componentData, mode);
    });

    // ✅ SYNC CHILDREN - chỉ 1 level (prevent loop)
    if (updatedElement.children?.length) {
        updatedElement.children = updatedElement.children.map(child => ({
            ...child,
            size: { ...child.size, [changedMode]: child.size?.[changedMode] || { width: 200, height: 50 } },
            position: { ...child.position, [changedMode]: child.position?.[changedMode] || { x: 0, y: 0, z: 1 } }
        }));
    }

    return updatedElement;
};

export const syncAllElements = (pageData, currentMode) => ({
    ...pageData,
    elements: pageData.elements.map(element => syncElementBetweenModes(element, currentMode))
});

/** ✅ GET RESPONSIVE - CLEAN & FIXED */
export const getResponsiveValues = (element, viewMode) => {
    const size = element.size?.[viewMode] || {
        width: element.type === 'section' ?
            (viewMode === 'mobile' ? 375 : viewMode === 'tablet' ? 768 : 1200) : 200,
        height: 400
    };

    const position = element.position?.[viewMode] || { x: 0, y: 0, z: 1 };

    // ✅ FIXED: Styles đúng thứ tự - base + mode-specific
    const baseStyles = element.styles || {};
    const modeStyles = element[`styles_${viewMode}`];
    const styles = viewMode === 'desktop' ? baseStyles : { ...baseStyles, ...modeStyles };

    // ✅ FIXED: ComponentData đúng merge
    const baseComponentData = element.componentData || {};
    const modeComponentData = element[`componentData_${viewMode}`];
    let componentData = viewMode === 'desktop' ?
        baseComponentData : { ...baseComponentData, ...modeComponentData };

    // ✅ BACKGROUND SYNC - ĐƠN GIẢN
    if (!componentData.backgroundType && styles.backgroundImage) {
        componentData = {
            ...componentData,
            backgroundType: 'image',
            backgroundImage: styles.backgroundImage.replace(/^url\(["']?([^"']+)["']?\)$/, '$1')
        };
    }

    return { size, position, styles, componentData };
};

export const initializeResponsiveData = (pageData) => ({
    ...pageData,
    elements: pageData.elements.map(element =>
        !element.position?.mobile ? syncElementBetweenModes(element, 'desktop') : element
    )
});

export const batchSyncElements = (elements, changedMode) =>
    elements.map(element => syncElementBetweenModes(element, changedMode));

export default {
    getScaleFactor, autoScaleSize, autoScalePosition, autoScaleStyles,
    autoScaleComponentData, syncElementBetweenModes, syncAllElements,
    getResponsiveValues, initializeResponsiveData, batchSyncElements
};
